export default {
    computed: {
        language() {
            return this.$store.getters.language;
        }
    },
    methods: {
        handleSetLanguage(lang) {
            this.$i18n.locale = lang;
            this.$store.dispatch("app/setLanguage", lang);
            //   this.$router.go(0)//刷新当前页面
            this.$message({
                message: "Switch Language Success",
                type: "success"
            });
        }
    }
};
