export default {
    name: 'bar',
    props: ['chartData'],
    data() {
        return {
            list: null
        }
    },
    mounted() {
        this.$nextTick(() => {
            this.drawLine()
        })
    },
    computed: {},
    methods: {
        drawLine() {
            var myChart = this.$echarts.init(this.$refs[this.chartData.id])
            // 绘制图表
            myChart.setOption({
                title: {},
                tooltip: {},
                xAxis: {
                    data: ['水', '电', '气']
                },
                yAxis: {},
                series: [
                    {
                        name: '能源',
                        type: 'bar',
                        data: [5, 20, 36],
                        itemStyle: {
                            color: 'skyblue'
                        }
                    }
                ]
            })
        }
    }
}
