import * as api from '@/api/project/spm/sparePartsPurchase/requestPurchase' // 增、删、改api引入
import Pagination from '@/components/Pagination'
import employeesSelect from '@/components/Select/employeesSelect' //下拉组件
import materialSelect from './materialSelect'
import orgSelect from '@/components/Select/orgSelect'
import { mapState } from 'vuex' // 基本数据引入

export default {
    name: 'requestPurchase',
    components: {
        Pagination,
        orgSelect,
        employeesSelect,
        materialSelect
    },
    data () {
        return {
            textarea: '',
            theight: 0, //表格高度
            isSingle: true, //表格是否单选 点击各按钮根据流程逻辑控制单多选
            materielData: [],
            list: [],
            fromList: [],
            total: 0,
            total2: 0,
            tableKey: [0, 1], //表格索引
            listQuery: {
                //查询
                currentPage: 1,
                pageSize: 10
            },
            listQuery2: {
                //从表查询
                requisitionsNumber: undefined,
                currentPage: 1,
                pageSize: 10
            },
            temp: {
                requisitionsType: 'drt002',
                procurementOrgCode: undefined,
                applyOrgCode: undefined,
                procurementOrgName: undefined,
                remark: undefined,
                requisitionerCode: undefined,
                requisitionerName: undefined,
                details: []
            },
            dialogStatus: '',
            employeeDialogVisible: false,
            materialDialogVisible: false,
            dialogFormVisible: false,
            selectedRows: null, //多选时选中的所有行
            selectedTempRows: null,
            rules: {
                requisitionsType: [
                    {
                        required: true,
                        message: '请购类型不能为空',
                        trigger: 'blur'
                    }
                ],
                requisitionerCode: [
                    {
                        required: true,
                        message: '请购人不能为空',
                        trigger: 'blur'
                    }
                ],
                procurementOrgCode: [
                    {
                        required: true,
                        message: '采购组织不能为空',
                        trigger: 'blur'
                    }
                ],
                applyOrgCode: [
                    {
                        required: true,
                        message: '申请单位不能为空',
                        trigger: 'blur'
                    }
                ]
            }
        }
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_requisitions_type: state => state.dict.dt_requisitions_type,
            dt_sp_requisitions_state: state =>
                state.dict.dt_sp_requisitions_state
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', [
            'dt_requisitions_type',
            'dt_sp_requisitions_state'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        setTimeout(() => {
            this.getList()
        }, 500)
    },
    methods: {
        getProcurementOrgCodeName (name) {
            if (name) this.temp.procurementOrgName = name
            else this.temp.procurementOrgName = undefined
        },
        getApplyOrgName (name) {
            if (name) this.temp.applyOrgName = name
            else this.temp.applyOrgName = undefined
        },
        handleMaterial () {
            this.materialDialogVisible = false
            this.temp.details = this.temp.details.concat(
                this.$refs.materiel.listSelected
            )
        },
        handleEmployee (val) {
            if (this.dialogFormVisible) {
                this.temp.requisitionerCode = val ? val.employeeCode : undefined
                this.temp.requisitionerName = val ? val.employeeName : undefined
            } else {
                this.listQuery.requisitionerCode = val ? val.employeeCode : undefined
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        getList () {
            //获取表格数据
            api.queryRecords(this.listQuery).then(res => {
                this.list = this.$myFun.codeToName(
                    res.data,
                    [this.dt_requisitions_type, this.dt_sp_requisitions_state],
                    ['requisitionsType', 'state']
                ).list
                this.total = res.data.pages.count
            })
        },
        resetTemp () {
            this.temp = {
                requisitionsType: 'drt002',
                procurementOrgCode: undefined,
                applyOrgCode: undefined,
                procurementOrgName: undefined,
                remark: undefined,
                requisitionerCode: undefined,
                requisitionerName: undefined,
                details: []
            }
        },
        handleQuery () {
            //表格查询
            this.listQuery.currentPage = 1
            this.getList()
            this.fromList = undefined
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        create () {
            //新增确认
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    if (this.temp.details.length == 0) {
                        this.$message({
                            title: '警告',
                            message: '请选择物料',
                            type: 'warning'
                        })
                        return
                    }
                    let str = this.temp.details.every(i => {
                        return i.demandDate && i.quantity
                    })
                    // measuringUnit
                    if (!str) {
                        this.$message({
                            title: '警告',
                            message: '请将表格信息填写完整',
                            type: 'warning'
                        })
                        return
                    }
                    this.temp.details = this.temp.details.map(i => {
                        return {
                            demandDate: i.demandDate,
                            materielCode: i.materielCode,
                            materielName: i.materielName,
                            measureCode: i.measuringUnit,
                            measureName: i.measuringUnitName,
                            specification: i.specification,
                            quantity: i.quantity
                        }
                    })
                    api.addRecords(this.temp).then(res => {
                        if (res.status == 201) {
                            this.$message({
                                title: '成功',
                                message: '新增成功',
                                type: 'success'
                            })
                            this.dialogFormVisible = false
                            this.getList()
                        }
                    })
                }
            })
        },
        handleUpdate () {
            //编辑按钮单击事件方法
            if (this.selectedRows) {
                this.temp = this.selectedRows
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update () {
            //编辑确认
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    if (this.temp.details.length == 0) {
                        this.$message({
                            title: '警告',
                            message: '请选择物料',
                            type: 'warning'
                        })
                        return
                    }
                    api.patchRecords(
                        this.temp.requisitionsNumber,
                        this.temp
                    ).then(res => {
                        if (res.status == 201) {
                            this.$message({
                                title: '成功',
                                message: '修改成功',
                                type: 'success'
                            })
                            this.dialogFormVisible = false
                            this.getList()
                        }
                    })
                }
            })
        },
        handleDelete () {
            //删除按钮单击事件方法
            if (this.selectedRows) {
                // srs20
                var str = this.selectedRows.every(item => {
                    return item.state !== "srs20"
                })
                if (!str) {
                    this.$message({
                        title: '警告',
                        message: '已提交信息不可删除',
                        type: 'warning'
                    })
                    return
                }
                this.$confirm(
                    '确定要删除选中的所有请购单吗？, 是否继续?',
                    '提示',
                    {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    }
                )
                    .then(() => {
                        var tmp = this.selectedRows.map(item => {
                            return item.requisitionsNumber
                        })
                        api.deleteRecords(tmp).then(res => {
                            if (res.status == 204) {
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success'
                                })
                                this.getList()
                            }
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handlePublish (row) {
            //发布
            if (row.state == 'srs20') {
                this.$message({
                    title: '警告',
                    message: '请购单已提交',
                    type: 'warning'
                })
                return
            }
            api.submitRecords(row.requisitionsNumber).then(res => {
                if (res.status == 201) {
                    this.$message({
                        title: '成功',
                        message: '提交成功',
                        type: 'success'
                    })
                    this.getList()
                }
            })
        },
        handleEditRow (row) {
            if (row.state == 'srs20') {
                this.$message({
                    title: '警告',
                    message: '请购单已提交，无法修改',
                    type: 'warning'
                })
                return
            }
            api.queryDetails({
                requisitionsNumber: row.requisitionsNumber
            }).then(res => {
                this.temp = row
                this.temp.details = res.data
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            })
        },
        selectChange (val) {
            this.selectedRows = val
            if (val.length == 0) {
                this.fromList = []
                return
            }
            this.getFromList()
        },
        selectTempChange (val) {
            this.selectedTempRows = val
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        //获取从表数据
        getFromList () {
            this.listQuery2.requisitionsNumber = this.selectedRows[
                this.selectedRows.length - 1
            ].requisitionsNumber
            api.queryDetails(this.listQuery2).then(res => {
                this.fromList = res.data.list
                this.total2 = res.data.pages.count
            })
        },
        handleCreateFrom () {
            this.materialDialogVisible = true
            setTimeout(() => {
                this.$refs.materiel.getData()
            }, 0)
        },
        handleDeleteFrom () {
            if (this.selectedTempRows && this.selectedTempRows.length) {
                this.temp.details = this.temp.details.filter(x => this.selectedTempRows.every(y => y.materielCode !== x.materielCode))
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条数据',
                    type: 'warning'
                })
            }
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        },
        rowTempClick (val) {
            this.$refs.tb2.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        }
    }
}
