import dialoggylc from "./dialog/dialoggylc"
import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import ImportFile from "@/components/srmImportFile"

export default {
    name: "delivery_Publish",
    components: { dialoggylc, ImportFile },
    props: ['rowData'],
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            total: 0,
            selectedrow: null,//主表选中行
            tableKey: 0, //表格索引，虚拟dom渲染表格时使用
            importDialogVisible: false,
            modalNo: 'EXTP-BAS-0014'  // 导入模板编号
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
    },
    methods: {
        setTabData (val) {
            this.list = val
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 185
        },
        //新增编辑删除成功之后刷新表格数据
        getFreshList () {
            let b = {
                rowId: this.rowId
            }
            api.updateRecords(b).then(res => {
                this.list = res.data.technologyInfoDTOList
                this.list.forEach((item)=>{
                    if(!item.creatorName){
                        item.creatorName = item.creator
                    }
                })
            })
        },
        //显示主表
        getList (row) {
            this.$refs.gylc.temp.supplierId = row.rowId
            this.rowId = row.rowId
            let a = {
                rowId: row.rowId
            }
            api.updateRecords(a).then(res => {
                this.list = res.data.technologyInfoDTOList
                this.list.forEach((item)=>{
                    if(!item.creatorName){
                        item.creatorName = item.creator
                    }
                })
            })
        },
        //新增按钮单击事件
        handleCreate () {
            this.$refs.gylc.resetTemp()
            this.$refs.gylc.dialogStatus = 'create'
            this.$refs.gylc.dialogVisible = true
        },
        //行内编辑按钮单击事件
        handleUpdate (index, val) {
            this.$refs.gylc.dialogStatus = 'update'
            this.$refs.gylc.dialogVisible = true
            this.$refs.gylc.temp = val
        },
        //删除
        handleDelete (index, val) {
            api.deleteRecordgylc(this.$refs.gylc.temp.supplierId, val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getFreshList()
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击表格某一行
        rowClick (val) {
            if (this.isSingle) {
                this.$refs.tb.clearSelection() //清除其他行的选中
            }
            this.$refs.tb.toggleRowSelection(val, "selected") //单击行绑定点击事件

        },
        //加载时默认选中第一行
        setFirstLine () {
            this.$nextTick(() => {
                this.$refs.tbmain.setCurrentRow(this.list[0])
            })
        },
        // 导入
        handleImport () {
            this.importDialogVisible = true
            this.$nextTick(() => {
                // 将rowId传给 srmImportFile
                this.$refs.importDialog.supplierId = this.rowData.rowId
                this.$refs.importDialog.creator = this.rowData.creatorName
                // 传递模板下载文件名称
                this.$refs.importDialog.excelName = '主要工艺流程.xlsx'
                this.$refs.importDialog.getList()
            })
        },
        // // 导出
        // handleExport () { 
        //     this.downloadLoading = true
        //     import('@/vendor/Export2Excel').then(excel => {
        //         const tHeader = ['工序号', '主要生产工艺流程', '产品关键特性', '过程关键特性', '备注', '创建人', '创建时间', '修改人', '修改时间']
        //         const filterVal = ['procedureNumber', 'technologicalProcess', 'productFeatures', 'processFeatures', 'remark', 'creatorName', 'createTime', 'updatorName', 'updateTime']
        //         const data = this.formatJson(filterVal, this.list)
        //         excel.export_json_to_excel({
        //             header: tHeader,
        //             data,
        //             filename: '主要工艺流程导出'
        //         })
        //         this.downloadLoading = true
        //     })
        // },
        // formatJson (filterVal, jsonData) {
        //     return jsonData.map(v => filterVal.map(j => {
        //         if (j === 'timestamp') {
        //             return 
        //         } else {
        //             return v[j]
        //         }
        //     }))
        // },
        // 弹框右上角关闭
        handleClose() {
            this.importDialogVisible = false
            // 清空已选文件列表
            this.$refs.importDialog.fileList = []
            this.getFreshList()
        }
    }
};
