import listCar from '../../../components/listCar'
import * as api from "@/api/project/iot/IOT/em"
import { mapState } from 'vuex'
export default {
    name: "accountInfo",
    props: {
        itemData: {
            type: Object,
            default: undefined
        },
        code: {
            type: String,
            default: undefined
        },
        row: {
            type: Object,
            default: undefined
        }
    },
    data () {
        return {
            form: {
                deviceCode: undefined,
                deviceName: undefined,
                brand: undefined,
                typeCode: undefined,
                manufacturersCode: undefined,
                modelNumber: undefined,
                leaveFactoryDate: undefined,
                remark: undefined,
            },
            temp: {
                deviceCode: undefined,
                deviceName: undefined,
                brand: undefined,
                typeCode: undefined,
                manufacturersCode: undefined,
                modelNumber: undefined,
                leaveFactoryDate: undefined,
                remark: undefined,
            },
            namePlate: null,
            namePlateFrom: {
                data: null
            },
        }
    },
    components: { listCar },
    watch: {
        itemData () {
            this.getList()
        }
    },
    computed: {
        ...mapState({
            // code: state => state.goPage.code,
            // subCode: state => state.goPage.subCode,
            iot_attribute_type: state => state.iotDict.iot_attribute_type,
            iot_nameplate_state: state => state.iotDict.iot_nameplate_state // 铭牌参数单位
        }),
        listLength: function () {
            return this.namePlate ? this.namePlate.length : false
        }
    },
    methods: {
        getList () {
            if (this.$route.name == "myProject") {
                Promise.all([api.queryProjectCode(this.code, this.itemData.deviceCode), api.queryNamePlate(this.code, this.itemData.deviceCode)]).then(
                    val => {
                        this.form = val[0].data
                        val[1].data = this.$myFun.srmCodeToName(val[1].data, [this.iot_nameplate_state], ['company'])
                        this.namePlate = val[1].data
                    }
                )
            }
        },
        resTemp () {
            this.temp = {}
        }
    }
};
