import *  as api from '@/api/project/eas/el/equipmentSpotCheck'
import Pagination from '@/components/Pagination'

export default {
    name: 'equipmentSpotCheck',
    components: { Pagination },
    props: ['orgCode', 'unMutiple', 'stockType', 'warehouseCode'],
    data () {
        return {
            list: null,
            total: 0,
            options: [],
            supplierData: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            activeName2: 'first',
            listSelected: [],
            tableKey: 0,
            allowSelect: false
        }
    },
    watch: {
        // 监听时间区间数组 写入到对应字段
        listSelected (value) {
            this.$parent.$parent.list = value
        }
    },

    mounted () {

        this.getEquipmentData()
        // this.suppierRecords()
    },
    methods: {
        //查设备类型
        getTypeCode () {
            api.queryTypeCode().then(res => {
                this.options = res.data
            })
        },
        // suppierRecords() {
        //     api.querySuppierRecords().then(res => {
        //         this.supplierData = res.data.list
        //     })
        // },

        handleQuery () {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
            this.listSelected = []
        },
        selectList (val) {
            this.selectListRow = val
            this.$emit("change", this.selectListRow)
        },
        // 父级工位表格展示已选，modal已选勾选
        getListSelected () {
            this.listSelected = this.$parent.$parent.list
            this.listSelected.map((v) => {
                v.deviceCode = v.childDeviceCode
            })
        },
        selectMain (val) {
            // 有选中检查是否已选
            for (let i = 0; i < this.listSelected.length; i++) {
                if (this.listSelected[i].childDeviceCode == val.deviceCode) {
                    // 在已选中去除该条
                    this.popSelected(val)
                    return false
                }
            }
            // 类型检查
            if (Array.isArray(val)) {
                return false
            }
            // 加入已选并勾选框
            this.listSelected.push(val)
            this.$refs.tb_bx.toggleRowSelection(val, true)
        },
        // 去除已选
        popSelected (val) {
            this.listSelected.forEach((item, index) => {
                if (item.childDeviceCode == val.deviceCode) {
                    this.listSelected.splice(index, 1)
                    this.$refs.tb_bx.toggleRowSelection(val, false)
                }
            })
        },
        selectAll (val) {
            if (val.length == 0) {
                this.listSelected = []
                this.$refs.tb_bx.clearSelection()
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.childDeviceCode === items.deviceCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        // 在备选中把已选打钩
        getEquipmentData () {
            api.queryMainlist(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                // 已选中的勾选状态
                this.listSelected.forEach(item => {
                    for (let i = 0; i < this.list.length; i++) {
                        if (item.childDeviceCode == this.list[i].deviceCode) {
                            this.$nextTick(() => {
                                this.$refs.tb_bx.toggleRowSelection(
                                    this.list[i],
                                    true
                                )
                            })
                            break
                        }
                    }
                })
            })
        }
    }
}
