import * as api from '@/api/project/mes/dataBase//warehouse'
import Pagination from '@/components/Pagination'

export default {
    name: 'Color',
    components: { Pagination },
    props: {
        warehouseCode: {
            type: String,
            required: true
        }
    },
    data() {
        return {
            list: null,
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                warehouseCode: undefined,
                locationCode: undefined,
                locationName: undefined
            },
            temp: {
                locationCode: undefined,
                locationName: undefined,
                warehouseCode: undefined,
                warehouseName: undefined,
                areaCode: undefined
            },

            locationCode: undefined,
            locationName: undefined,
            warehouseName: undefined,
            areaCode: undefined,
            selectCode: [],
            dialogStatus: '',
            tableKey: 0
        }
    },
    mounted() {
        this.getList()
    },
    methods: {
        getList() {
            api.queryLocation(this.warehouseCode, this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        handleQuery() {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        // 获取表格选中时的数据
        selectRow(val) {
            this.selectlistRow = val
            this.$emit('handSelectAreasRows', val)
        },
        create() {
            //新增确认按钮
            this.$refs.temp.validate(valid => {
                if (valid) {
                    api.addRecord(this.temp).then(() => {
                        this.getList()
                        this.dialogFormVisible = false
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                    })
                } else {
                    return false
                }
            })
        },
        cancel() {
            //取消弹窗按钮
            this.dialogFormVisible = false
        },
        resetTemp() {
            this.temp = {
                colorCode: undefined,
                colorName: undefined,
                colorAbbreviation: undefined,
                state: 1,
                remark: undefined
            }
        },
        handleClose() {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        handleSelectionChange(selection) {
            this.selectCode = selection.map(v => v.colorCode)
        }
    }
}
