import Pagination from '@/components/Pagination' // 分页组件
import * as api from '@/api/project/wms/stokMove/stokMoveFunc'
import { mapState } from 'vuex'
import AuthoType from '@/components/Select/AuthoType.vue'//权限类型下拉组件
import qrcode from "@/components/QRCode"
import wlxzbykcz from '@/components/Select/wlxzbykcz'//库存帐物料信息模块


export default {
    name: "issueOut",
    components: { Pagination, wlxzbykcz, AuthoType, qrcode },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            options: [],
            options1: [],
            unMutiple: false,
            value: '',
            areaList: null,
            locatorList: [],
            toareaList: null,
            tolocatorList: [],
            list: [],
            list1: null,
            list2: null,
            total: 0,
            total1: 0,
            total2: 0,
            activeName2: 'first',
            createTime: [this.currentTime(), this.currentTime()],
            accountDate: [this.currentTime(), this.currentTime()],
            isEdit: false,
            disabled: false,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                orgCode: undefined,
                orgName: undefined,
                dicType: undefined,
                warehouseCode: undefined,
                accountDate: this.timeFormat(),
                toWarehouseCode: undefined,
                toOrgCode: undefined,
            },
            listQuery1: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                dicType: undefined,
                warehouseCode: undefined,
                toWarehouseCode: undefined,
                proofNo: undefined,
            },
            dto: {
                accountDate: undefined,
                businessType: undefined,
                orgCode: undefined,
                orgName: undefined,
                outOrderPlans: [],
                toOrgCode: this.orgCode,
                toOrgName: undefined,
                toWarehouseCode: undefined,
                warehouseCode: undefined,
            },
            temp: {
                orgCode: undefined,
                dicType: undefined,
                warehouseCode: undefined,
                accountDate: undefined,
                toWarehouseCode: undefined,

            },
            locationCode: undefined,
            proofNo: undefined,
            warehouseCode: undefined,
            orgCode: undefined,
            parentsRows: null,
            listQuery2: {
                proofNo: undefined,
                currentPage: 1,
                pageSize: 10,
            },
            printData: {},//打印数据  
            value1: '',
            dialogStatus: '',
            wlxzDialogFormVisible: false,
            dialogPrintVisible: false,
            tableKey: 0,
        }
    },
    watch: {
        'listQuery.orgCode': {
            handler (newValue) {
                this.listQuery.orgCode = newValue
                this.listQuery.warehouseCode = ''
                this.listQuery.toWarehouseCode = ''
            },
        },
        'listQuery.warehouseCode': {
            handler (newValue) {
                this.listQuery.warehouseCode = newValue
            },
            deep: true,
            immediate: true
        },
        'listQuery.toWarehouseCode': {
            handler (newValue) {
                this.listQuery.toWarehouseCode = newValue
            },
            deep: true,
            immediate: true
        },
    },
    computed: {
        ...mapState({
            dt_materiel_type: state => state.dict.dt_materiel_type,
            dt_materiel_classify: state => state.dict.dt_materiel_classify,
            dt_business_type_screenGroupNum_yk: state => state.businessComponent.dt_business_type_screenGroupNum_yk
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch('dict/getDicData', ['dt_materiel_type', 'dt_materiel_classify'])
        this.$store.dispatch('businessComponent/getBusinessComponentData', ['dt_business_type_screenGroupNum_yk'])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 30
            this.pheight = this.$myFun.getSingleTbHeight() + 45
            this.sheight = this.$myFun.getSingleTbHeight() - 75
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        currentTime () {
            var date = new Date()
            var seperator1 = "-"
            var year = date.getFullYear()
            var month = date.getMonth() + 1
            var strDate = date.getDate()
            if (month >= 1 && month <= 9) {
                month = "0" + month
            }
            if (strDate >= 0 && strDate <= 9) {
                strDate = "0" + strDate
            }
            var currentdate = year + seperator1 + month + seperator1 + strDate
            return currentdate
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.toWarehouseCode = val
                this.listQuery.businessType = this.dt_business_type_screenGroupNum_yk.options[0].wareRuleCode
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQuery1.orgCode = val
            } else if (i === 3) {
                this.listQuery1.warehouseCode = val
                this.listQuery1.toWarehouseCode = val
                this.listQuery1.dicType = this.dt_business_type_screenGroupNum_yk.options[0].wareRuleCode
            }
        },
        getList1 () {//查询凭证号
            if (this.accountDate) {
                this.listQuery1.accountDateStart = this.accountDate[0]
                this.listQuery1.accountDateEnd = this.accountDate[1]
            } else {
                this.listQuery1.accountDateStart = ''
                this.listQuery1.accountDateEnd = ''

            }

            // const createTime = this.listQuery1.createTime
            if (this.createTime) {
                this.listQuery1.createDateStart = this.createTime[0]
                this.listQuery1.createDateEnd = this.createTime[1]
            } else {
                this.listQuery1.createDateStart = ""
                this.listQuery1.createDateEnd = ""

            }
            this.list2 = []
            api.queryRecords1(this.listQuery1).then(res => {
                res = res.data
                this.list1 = res.list
            })
        },
        getList2 () {//已选项查询
            api.queryRecords(this.listQuery2).then(res => {
                res = res.data
                this.list2 = res.list
                this.total = res.pages.count
            })
        },
        selectRow1 (val) {
            this.selectRowData1 = val
        },
        changeOrg () {//组织编码下拉change
            this.listQuery1.orgCode = this.listQuery.orgCode
        },
        changeToWa () {//仓库编码下拉change
            this.listQuery.toWarehouseCode = this.listQuery.warehouseCode
            this.listQuery1.warehouseCode = this.listQuery.warehouseCode
            this.listQuery1.toWarehouseCode = this.listQuery1.warehouseCode
        },
        changeBusinessType () {//业务类型下拉change
            this.listQuery1.dicType = this.listQuery.businessType
        },
        handleCreate () {//新增弹窗
            if (this.listQuery.orgCode && this.listQuery.businessType && this.listQuery.accountDate && this.listQuery.warehouseCode && this.listQuery.toWarehouseCode) {
                this.wlxzDialogFormVisible = true
                this.orgCode = this.listQuery.orgCode
                this.warehouseCode = this.listQuery.warehouseCode
                this.$nextTick(function () {
                    this.$refs.wlxzRef.listSelected = [...this.list]
                    this.$refs.wlxzRef.isEdit = true
                    this.$refs.wlxzRef.getList()
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.selectQueryConditions"),
                    type: "warning"
                })
            }
            api.getArea(this.listQuery.toWarehouseCode).then(resa => {
                resa = resa.data
                this.toareaList = resa
            })
            api.getArea(this.warehouseCode).then(resa => {
                resa = resa.data
                this.areaList = resa
            })
        },
        materielTables (row) {
            this.parentsRows = row
        },
        handleUpdate () {//保存
            let result = true
            let groupList = []
            this.list.forEach((v) => {
                if (!v.toWareAreaCode || !v.toWareLocationCode) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: this.$t("message.selectionLocationReserve"),
                        type: "warning"
                    })
                    result = true
                }
                if (v.outStockNum > 0) {
                    groupList.push(v)
                    result = false
                    this.dto.outOrderPlans = groupList
                    if (result) {
                        this.$message({
                            title: this.$t("message.warning"),
                            message: this.$t("message.deliveryCannotBeZeroOrEmpty"),
                            type: "warning"
                        })
                    }
                }
            })
            this.listQuery.toOrgCode = this.listQuery.orgCode
            this.dto = this.listQuery
            this.dto.outOrderPlans = groupList
            api.addRecords(this.dto).then(() => {
                this.disabled = true
                this.$message({
                    title: this.$t("message.success"),
                    message: this.$t("message.saveSuccessfully"),
                    type: 'success',
                    duration: 2000
                })
            })
        },
        update () {
            if (this.$refs.wlxzRef.listSelected.length > 0) {
                this.list = this.$refs.wlxzRef.listSelected.map(v => {
                    v.measuringUnit = v.unitName
                    v.specification = v.unit
                    v.moveNum = v.availableNum
                    return v
                })
                this.wlxzDialogFormVisible = false
                this.isEdit = true
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handleDelete () {//删除列表
            if (this.selectRowData1 && this.selectRowData1.length == 1) {
                this.$confirm(this.$t("message.isDeleteContinue"), this.$t("message.prompt"), {
                    confirmButtonText: this.$t("message.confirm"),
                    cancelButtonText: this.$t("message.cancel"),
                    type: 'warning',
                }).then(() => {
                    for (let i = 0; i < this.list.length; i++) {
                        if (this.list[i].materielCode === this.selectRowData1[0].materielCode) {
                            this.list.splice(i, 1)
                            this.$message({
                                title: this.$t("message.success"),
                                message: this.$t("message.deleteSuccessed"),
                                type: 'success',
                                duration: 2000
                            })
                        }
                    }
                }).catch(() => {
                    this.$message({
                        type: 'info',
                        message: this.$t("message.deleteCanceled")
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedoneplease"),
                    type: 'warning'
                })
            }
        },
        handelRefresh () {//刷新
            this.isEdit = false
            this.disabled = false
            this.list = []
            // this.listQuery=[]
        },
        selectRowchange (val) {
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }
            // this.$refs.tb.setCurrentRow(this.selectlistRowProof)
            if (this.selectlistRowchange) {
                this.listQuery2.outOrderNumber = this.selectlistRowchange.outOrderNumber
                this.getList2()
            }
        },
        clickProofNo (val) {
            this.clicklistProofNo = val
            this.listQuery2.outOrderNumber = this.clicklistProofNo.outOrderNumber
            this.$refs.tbb.clearSelection()
            this.$refs.tbb.toggleRowSelection(val) //单击行绑定点击事件
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowchange) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowchange
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.outOrderNumber)
                })
            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }
        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)

        },
        //导出
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '凭证号', '业务类型', '物料编码', '物料名称',
                    '数量', '组织编码', '仓库编码', '区位',
                    '储位', '往来仓库', '往来区位', '往来储位',
                    '库存类型', '库存状态', '记账日期', '创建日期',
                    '记账人',
                ]
                const filterVal = [
                    'proofNo', 'businessType', 'materielCode', 'materielName',
                    'outStockNum', 'orgCode', 'warehouseCode', 'wareAreaCode',
                    'wareLocationCode', 'toWarehouseCode', 'toWareAreaCode', 'toWareLocationCode',
                    'stockType', 'stockState', 'accountDate', 'createTime',
                    'creator',
                ]
                const data = this.formatJson(filterVal, this.list2)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))

        },
        handleFilter1 () {
            this.getList1()
        },
        tochangeAreaSel (areaCode, i) {
            api.getLocator(this.listQuery.toWarehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.tolocatorList, i, res)
                this.$set(this.list[i], 'toWareLocationCode', res[0].locationCode)
                this.$set(this.list, i, this.list[i])
            })

        },

    }
};
