import * as api from "@/api/data-base/dict";
import { DICT_TYPE_OPTIONS } from "@/utils/constant";
import { mapState } from "vuex";
export default {
    name: "dict",
    components: {},
    data () {
        return {

            theight: 0,
            stheight: 0,//右子表高度
            isSingle: false,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],

            mainList: {},
            total: 0,
            totalSub: 0,
            activeCollNames: ['1'],
            activeCollNamesub: ['2'],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                dictCode: undefined,
                dictName: undefined,
                dictType: undefined,
                dictItemKey: undefined,
                dictItemValue: undefined
            },

            enableds: {
                areaEnabled: false,
                locationEnabled: false,
                wmsEnabled: false,
            },
            temp: {
                dictCode: undefined,
                dictName: undefined,

                dictType: undefined,
                dictItemDTOs: []
            },
            dictCode: undefined,
            selectCode: [],
            tableKey: [0, 1],//表格索引，虚拟dom渲染表格时使用
            dictTypeOptions: DICT_TYPE_OPTIONS,
            dictItems: []
        };
    },
    computed: {
        ...mapState({
            dt_data_state: state => state.dict.dt_data_state,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", [
            "dt_data_state"
        ]);
        this.setTableHeight();
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        };
        this.getList();
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 2;
            this.stheight = this.theight + 58;
        },
        //模板变化
        changeMoudle (val) {
            this.$emit('changeSelect', val)
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list;
                this.total = res.data.pages.count;
            });
        },
        handleQuery () {
            this.listQuery.currentPage = 1;
            this.getList();
        },
        handleSave () {

        },
        handleCreate () {
            let newValue = {
                dictCode: '',
                dictName: '',

            }
            this.list.push(newValue)
        },

        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection(); //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected"); //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val;
                    this.currentSelectedRow = val[val.length - 1]
                }
            } else {
                this.selectedRows = val;
                this.currentSelectedRow = val[val.length - 1]
            }
        },
        //点击某行时执行
        rowClick (val) {
            if (this.isSingle) {
                this.$refs.tb.clearSelection(); //清除其他行的选中
            }
            this.$refs.tb.toggleRowSelection(val); //单击行绑定点击事件
        },
        handleUpdate () { //编辑弹窗

        },
    }
};
