import * as api from '@/api/project/eas/el/inventoryPlanning'
import Pagination from '@/components/Pagination'
import deviceType from '../deviceTypeSelect'
import { mapState } from 'vuex'

export default {
    name: 'eqmtSelect',
    components: { Pagination, deviceType },
    props: {
        // 是否禁用选择设备类型（需要在父组件中关联进行控制时使用）
        deviceTypeDisable: {
            type: Boolean,
            default: false
        },
        // // 父组件中已选记录集合
        listSelected: {
            type: Array,
            default: () => {
                return []
            }
        },
        organizationCode: {
            type: String,
            default: () => {
                return ''
            }
        },
        deviceTypeCode: {
            type: String,
            default: () => {
                return ''
            }
        }
    },
    data () {
        return {
            // 设备列表（备选列表）
            list: [],
            currentSelect: [],
            // 设备类型
            options: [],
            total: 0,
            // 查询条件
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                typeCode: undefined,
                deviceName: undefined,
                organizationCode: undefined
            },
            // 当前tab
            activeName2: 'first',
            // 当前弹框弹出后已选记录集合
            tableKey: 0
        }
    },
    watch: {
        // 设备类型改变时查询对应设备
        'listQuery.typeCode' () {
            this.getList()
        },
        'deviceTypeCode' (val) {
            this.listQuery.typeCode = val
        },
        dt_device_state () {
            if (this.list.length) {
                let options = [this.dt_device_state]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['state']
                )
            }
        }
    },
    computed: {
        // 设备类别字典
        ...mapState({
            dt_device_state: state => state.eamDict.dt_device_state,
            dt_device_category: state => state.eamDict.dt_device_category
        })
    },
    mounted () {
        this.$store.dispatch('eamDict/getDicData', ['dt_device_state', 'dt_device_category'])
        this.getList()
    },
    methods: {
        getDevType (val) {
            this.listQuery.typeCode = val.typeCode
        },
        getList (organizationCode) {
            this.listQuery.deviceCodes = this.listSelected
                .map(item => {
                    return item.deviceCode
                })
                .join(',')
            // 获取当前查询条件下的设备
            this.listQuery.organizationCode = organizationCode
                ? organizationCode
                : this.organizationCode
            // this.listQuery.typeCode = this.deviceTypeCode ? this.deviceTypeCode : undefined
            api.queryMainlist(this.listQuery).then(res => {
                this.list = res.data.list
                let options = [this.dt_device_state]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['state']
                )
                this.total = res.data.pages.count
                setTimeout(() => {
                    // 翻页后对当前已选打钩
                    this.getData()
                }, 0)
            })
        },
        handleQuery () {
            // 翻页
            this.listQuery.currentPage = 1
            this.getList()
        },
        checkUnsel (val, row) {
            // 当前选择记录是否已选
            let isuncheck = true
            val.forEach(item => {
                if (item.deviceCode === row.deviceCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            // 点击备选记录
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.currentSelect) {
                    val.forEach((items, indexv) => {
                        let isSelected = false
                        this.currentSelect.forEach(item => {
                            if (item.deviceCode == items.deviceCode) {
                                isSelected = true
                            }
                        })
                        if (!isSelected) {
                            this.currentSelect.push(val[indexv])
                        }
                    })
                } else {
                    this.currentSelect = val
                }
            } else {
                this.currentSelect = this.currentSelect.filter(item => {
                    return item.deviceCode != row.deviceCode
                })
            }
        },
        selectAll (val) {
            // 全选
            if (val.length == 0) {
                this.list.forEach(item => {
                    this.currentSelect.forEach((item2, index) => {
                        if (item.deviceCode == item2.deviceCode) {
                            this.currentSelect.splice(index, 1)
                        }
                    })
                })
                return
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.currentSelect.forEach(item => {
                    if (item.deviceCode === items.deviceCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.currentSelect.push(val[indexv])
                }
            })
        },
        getData () {
            // 翻页后当前已选打钩
            if (this.currentSelect && this.currentSelect.length) {
                this.currentSelect.forEach(selected => {
                    this.list.forEach((wait, index) => {
                        if (wait.deviceCode == selected.deviceCode) {
                            this.$nextTick(() => {
                                this.$refs.tb_bx.toggleRowSelection(
                                    this.list[index],
                                    true
                                )
                            })
                        }
                    })
                })
            }
        }
    }
}
