import { mapState } from 'vuex'
export default {
    name: 'tabWLcost',
    props: ['tabWLcostList'],
    data () {
        return {
            type: null,
            tableData: [],
        }
    },
    computed: {
        ...mapState({
            dt_srm_pdm: state => state.dict.dt_srm_pdm,
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_srm_pdm'])
    },
    watch: {
        tabWLcostList (val) {
            console.log(val, 'transportType')
            let options = [this.dt_srm_pdm]
            val = this.$myFun.srmCodeToName(val, options, ['transportType'])
            if (this.type == 'add') {
                if (val && val.length > 0) {

                    let arr = JSON.parse(JSON.stringify(val))
                    arr.forEach(i => {
                        i.actuarialEachShipmentNumber = i.eachShipmentNumber ? i.eachShipmentNumber : 0 //件数精算
                        i.actuarialSingleCost = i.singleCost ? i.singleCost : 0 //件数精算
                        i.actuarialSubtotalCost = i.actuarialSingleCost / i.actuarialEachShipmentNumber//小计精算
                    })
                    this.tableData = arr
                } else {
                    this.tableData = []
                }
            } else {

                let arr = JSON.parse(JSON.stringify(val))
                this.tableData = arr
            }


        }
    },
    methods: {
        checkValue (val) {
            if (isNaN(val) || val == Infinity) {
                return undefined
            } else {
                if (val.toString().indexOf('.') !== -1) {
                    val = val.toFixed(2)
                }
                return val
            }
        },
        getSummaries (param) {
            const { columns, data } = param
            const sums = []
            columns.forEach((column, index) => {
                if (index === 0) {
                    sums[index] = '总计'
                    return
                }
                const values = data.map(item => Number(item[column.property]))
                if (!values.every(value => isNaN(value))) {
                    if (column.property == 'transportTypeCN' || column.property == 'actuarialEachShipmentNumber' || column.property == 'actuarialSingleCost' || column.property == 'actuarialSubtotalCost') {
                        sums[index] = values.reduce((prev, curr) => {
                            const value = Number(curr)
                            if (!isNaN(value)) {
                                return prev + curr
                            } else {
                                return prev
                            }
                        }, 0)
                        // sums[index] += ' 元'
                    } else {
                        sums[index] = '--'
                    }
                } else {
                    sums[index] = 0
                }
            })
            columns.forEach((val, index) => {
                //小计精算
                if (val.property == 'actuarialSubtotalCost') {
                    this.$store.dispatch('getData/getWlData', sums[index])
                }
            })
            return sums
        },
        getPriceCost (row) {
            this.tableData.forEach(i => {
                if (i.rowId == row.rowId) {
                    i.actuarialSubtotalCost = this.checkValue(i.actuarialSingleCost / i.actuarialEachShipmentNumber)//小计精算
                }
            })
        },
        initData (val) {
            this.type = val
            this.tableData = []
        }
    }
}
