import * as api from "@/api/project/srm/supplierManagement/supplierRegister"

export default {
    data () {
        let validatePercentage = (rule, value, callback) => {
            const reg = /^(100|[1-9]?\d(\.\d\d?\d?)?)$|0$/
            if (value == '' || value == undefined || value == null) {
                callback()
            } else {
                if ((!reg.test(value)) && value != '') {
                    callback(new Error('请输入正确的百分比'))
                } else {
                    callback()
                }
            }
        }
        return {
            dialogStatus: '',
            dialogVisible: false,
            temp: {},
            rules: {
                name: [{ required: true, message: '二级供应商名称不能为空', trigger: 'blur' }],
                product: [{ required: true, message: '二级供应商产品不能为空', trigger: 'blur' }],
                address: [{ required: true, message: '二级供应商地址不能为空', trigger: 'blur' }],
                zeroFaultRate: [
                    { validator: validatePercentage, trigger: "blur" }
                ],
                trimesterFaultRate: [
                    { validator: validatePercentage, trigger: "blur" }
                ],
                twelvemonFaultRate: [
                    { validator: validatePercentage, trigger: "blur" }
                ],
            }
        }
    },
    methods: {
        create () {
            this.$refs.dataForm.validate(valid => {
                if (valid) {
                    api.addRecordsecondary(this.temp).then(() => {
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.dialogVisible = false
                        this.$emit('getFreshList')
                    })
                } else {
                    return false
                }
            })

        },
        update () {
            api.updateRecordsecondary(this.temp.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.$emit('getFreshList')
            })
        },
        resetTemp () {
            this.temp = {
                name: undefined,
                product: undefined,
                address: undefined,
                qseAuth: undefined,
                price: undefined,
                zeroFaultRate: undefined,
                trimesterFaultRate: undefined,
                twelvemonFaultRate: undefined,
                supplierId: this.temp.supplierId
            }
        }
    }
}
