import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from 'vuex'
import * as api from "@/api/project/srm/contractManage/evaluationModel"
import throttle from 'lodash/throttle'

export default {
    components: { caiGou },
    data () {
        return {
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            temp: {},
            DICT_CODE: DICT_CODE,
            dialogStatus: '',
            dialogVisible: false,
            dialogAddVisible: false,
            params: {}
        }
    },
    computed: {
        ...mapState({
            dt_srm_pak: state => state.dict.dt_srm_pak,
            dt_srm_pb1: state => state.dict.dt_srm_pb1,
            dt_srm_pb2: state => state.dict.dt_srm_pb2,
            dt_srm_pb4: state => state.dict.dt_srm_pb4,
            dt_srm_pas: state => state.dict.dt_srm_pas,
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', ['dt_srm_pak', 'dt_srm_pb1', 'dt_srm_pb2', 'dt_srm_pb4', 'dt_srm_pas'])
    },
    methods: {
        getList () {
            api.queryRecordTree(this.params).then(res => {
                let options = [this.dt_srm_pb4]
                res.data = this.$myFun.srmCodeToName(res.data, options, ['evaluationItem'])
                this.list = res.data
            })
        },
        //获取采购组织名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //新增
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogAddVisible = true
        },
        //编辑
        handleUpdate () {
            if (this.selectlistRow) {
                this.dialogStatus = 'update'
                this.dialogAddVisible = true
                this.temp = this.selectlistRow
                this.temp.parentCode = this.params.parentCode
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //删除deleteRecord
        handleDelete () {
            if (this.selectlistRow) {
                api.deleteRecord(this.selectlistRow.rowId).then(() => {
                    this.$message({
                        title: '警告',
                        message: '删除成功',
                        type: 'warning'
                    })
                })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        //新增弹框确定按钮单击事件
        create: throttle(function () {
            this.temp.parentCode = this.params.parentCode
            api.addRecord(this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogAddVisible = false
                this.getList()
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //编辑弹框确定按钮单击事件
        update: throttle(function () {
            this.temp.parentCode = this.params.parentCode
            api.updateRecord(this.selectlistRow.rowId, this.temp).then(() => {
                this.$message({
                    title: '成功',
                    message: '编辑成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogAddVisible = false
                this.getList()
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectRow (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
        resetTemp () {
            this.temp = {
                purchaseOrg: undefined,
                type: undefined,
                classification: undefined,
                category: undefined,
                state: undefined,
                classificationCode: undefined,
                parentCode: undefined,
                divideItem: undefined,
                name: undefined,
                remark: undefined

            }
        }
    }
}
