import * as api from '@/api/project/ems/onlineMonitoring/online.js'
export default {
    name: 'InstrumentMonitor',
    data () {
        return {
            isMore: false,
            list: [],
            total: 0,
            loading: true,
            listQuery: {
                warningName: 'modelWarning'
            },
            setTimeoutValue: null
        }
    },
    watch: {
        list: {
            handler () { },
            immediate: true
        }
    },
    computed: {
        holder () {
            return this.list.length % 5 == 0 ? 0 : 5 - (this.list.length % 5)
        }
    },
    mounted () {
        this.getList()
    },
    destroyed () {
        this.clear()
    },
    methods: {
        showMore () {
            this.isMore = !this.isMore
        },
        //获取表格数据
        getList () {
            api.query(this.listQuery).then(response => {
                this.list = response.data
                this.loading = false
            })
        },
        setTimeout () {
            this.setTimeoutValue = setInterval(() => {
                this.getList()
            }, 60000 * 5)
        },
        clear () {
            if (this.setTimeoutValue) {
                clearInterval(this.setTimeoutValue)
            }
        }
    }
}
