import throttle from 'lodash/throttle'
import Pagination from '@/components/Pagination'
import * as api from '@/api/project/iot/MOM/logService'
export default {
    components: { Pagination },
    data() {
        return {
            time: '',
            listQuery: {
                currentPage: 1,
                pageSize: 15,
                startTime: '',
                endTime: '',
                userName: '',
                ip: ''
            },
            listLoading: false,
            tableData: [],
            theight: 0,
            total: 0
        }
    },
    mounted() {
        this.theight = this.$myFun.getSingleTbHeight() - 30
        window.onresize = () => {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        }
        this.handleQuery()
    },
    methods: {
        //表格查询
        handleQuery: throttle(
            function() {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        getList() {
            this.listLoading = true
            if (this.time) {
                this.listQuery.startTime = this.time[0]
                this.listQuery.endTime = this.time[1]
            }
            api.getServer(this.listQuery).then((res) => {
                this.tableData = []
                res.data.list.map((item) => {
                    if (item.logInfo) {
                        this.tableData.push(item.logInfo)
                    }
                })
                this.total = res.data.total
                this.listLoading = false
            })
        }
    }
}
