import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
export default {
    name: "jsgl",
    components: { Pagination, caiGou, dialogPersonSelect },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            supplier: undefined,
            DICT_CODE: DICT_CODE,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15,
                confirmState: 'PDN1'
            },
            queryState: ['PA1W', 'PA1Q'],//查潜在和注册供应商
            dialogVisible: false,
            dialogVisiblesh: false,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    computed: {
        ...mapState({
            dt_srm_pa3: state => state.dict.dt_srm_pa3,
            dt_srm_pa1: state => state.dict.dt_srm_pa1,
            dt_srm_pdn: state => state.dict.dt_srm_pdn,
            dt_srm_pay: state => state.dict.dt_srm_pay,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pa3", 'dt_srm_pa1', 'dt_srm_pdn', 'dt_srm_pay'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'dt_srm_pa3': function () {
            this.getList()
        }
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取采购组织名称
        selectName (val) {
            this.listQuery.purchaseOrgName = val
        },
        handlePerson () {
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            console.log(val[0].employeeName)
            // this.$set(this.listQuery, 'assignPerson', val[0].employeeCode)
            this.$set(this.listQuery, 'purchaser', val[0].employeeName)
        },
        //获取表格数据
        getList () {
            this.listQuery.platformState = this.queryState.join(",")
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pa1, this.dt_srm_pdn, this.dt_srm_pay]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['platformState', 'confirmState', 'frozenState'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.$emit('setFormationState', 'add')
            this.$emit('confirmState', 'edit')
        },
        //主营指定按钮单击事件
        handleConfirm () {
            if (this.selectlistRow) {
                if (this.selectlistRow.purchaseOrg && this.selectlistRow.purchaser) {
                    this.dialogVisible = true
                    this.supplier = this.selectlistRow.supplierName
                } else if (this.listQuery.purchaseOrg && this.listQuery.purchaser) {
                    this.dialogVisible = true
                    this.supplier = this.selectlistRow.supplierName
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择采购组织,采购人',
                        type: 'warning',
                        duration: 2000
                    })
                }

            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        confirm () {
            let dto = {
                method: 'appoint',
                purchaseOrg: this.listQuery.purchaseOrg,
                purchaser: this.listQuery.purchaser,
                purchaseOrgName: this.listQuery.purchaseOrgName
            }
            api.confirmRecord(this.selectlistRow.rowId, dto).then(() => {
                this.$message({
                    title: '成功',
                    message: '指定成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisible = false
                this.listQuery.purchaseOrg = undefined
                this.listQuery.purchaser = undefined
                this.getList()
            })
        },
        //审核按钮单击事件
        handleAudit () {
            if (this.selectlistRow) {
                if (this.selectlistRow.purchaseOrg && this.selectlistRow.purchaser) {
                    this.dialogVisiblesh = true
                } else if (this.listQuery.purchaseOrg && this.listQuery.purchaser) {
                    this.dialogVisiblesh = true
                } else {
                    this.$message({
                        title: '警告',
                        message: '请选择采购组织,采购人',
                        type: 'warning',
                        duration: 2000
                    })

                }

            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        Audit () {
            let dto = {
                method: 'check',
                purchaseOrg: this.listQuery.purchaseOrg,
                purchaser: this.listQuery.purchaser
            }
            api.confirmRecord(this.selectlistRow.rowId, dto).then(() => {
                this.$message({
                    title: '成功',
                    message: '审核成功',
                    type: 'success',
                    duration: 2000
                })
                this.dialogVisiblesh = false
                this.listQuery.purchaseOrg = undefined
                this.listQuery.purchaser = undefined
                this.getList()
            })
        },
        handleUpdate (index, row) {
            //编辑按钮单击事件方法
            this.$emit('setFormationState', 'edit', row)
            this.$emit('confirmState', 'edit', row)
        },
        handleDelete (index, val) {
            //删除按钮单击事件方法
            api.deleteRecord(val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getList()
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
