import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import { mapState } from "vuex"
import dialogOrgSelect from '@/components/Dialog/dialogOrgSelect'
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import dialogWlxzbycglx from '@/components/Dialog/dialogWlxzbycglx.vue'//物料选择
export default {
    components: { caiGou, dialogGysxz, dialogOrgSelect, dialogPersonSelect, dialogWlxzbycglx },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            dialogVisible: false,
            dialogStatus: '',
            tableKey: 0,
            list: [],
            temp: {},
            radio: {
                value: '1',
            }
        }
    },
    computed: {
        ...mapState({
            dt_srm_pc2: state => state.dict.dt_srm_pc2,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pc2"])
        this.getList()
    },
    methods: {
        //获取采购组织名称
        selectName (val) {
            this.temp.purchaseOrgName = val
        },
        //申请单位
        showOrg () {
            this.$refs.refOrg.dialogVisible = true
        },
        //申请单位确定
        confirmOrg (data) {
            console.log(data)
            if (data) {
                this.$set(this.temp, 'editOrgCode', data[0].organizationCode)
                this.$set(this.temp, 'number6', data[0].organizationName)
            }
        },
        //人员选择
        showPerson () {
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            this.$set(this.temp, 'purchaseSupporter', val[0].employeeCode)
            this.$set(this.temp, 'number7', val[0].employeeName)
        },
        //供应商
        showGys () {
            this.$refs.refGys.dialogVisible = true
            this.$refs.refGys.getList()
        },
        //供应商确定
        handleGysClick (val) {
            this.$set(this.temp, 'supplierCode', val[0].supplierCode)
            this.$set(this.temp, 'supplierName', val[0].supplierName)
        },
        getList () {
            // this.list = [
            //     { params1: '1', params2: 'LL_X7-98880', params3: 'LL_X7-98880', params4: '01', params5: '', params6: '', params7: '', params8: '', params9: '', params10: '', }
            // ]
        },
        handleCreate () {
            this.$nextTick(() => {
                this.$refs.wlxz.listSelected = [...this.list]
                this.$refs.wlxz.getList()
            })
            this.$refs.wlxz.dialogVisible = true
        },
        //物料选择确定
        confirmWlxz (val) {
            console.log(val)
            this.$nextTick(() => {
                this.list = [...val]
                this.$refs.wlxz.dialogVisible = false
            })

        },
        handleDelete (index) {
            this.$confirm("确认要删除该数据项吗?", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                this.list.splice(index, 1)
                this.$message({
                    type: "success",
                    message: "删除成功"
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        createData () {

        },
        updateData () {

        }
    }
}
