import * as api from '@/api/project/eas/database/customer'
import Pagination from '@/components/Pagination'
import { TWO_STATE_OPTIONS } from '@/utils/constant'
import { validatorContactNumber, validatorEmail } from '@/utils/validate'
import { mapState } from 'vuex';
import ImportFile from "@/components/ImportFile";


export default {
    name: 'customer',
    components: { Pagination, ImportFile },
    data () {
        return {
            list: null,
            total: 0,
            theight: 0,//表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                customerCode: undefined,
                customerName: undefined,
                customerType: undefined
            },
            temp: {
                customerCode: undefined,
                customerName: undefined,
                customerType: undefined,
                customerProperty: undefined,
                isRetailer: 0,
                address: undefined,
                contact: undefined,
                contactNumber: undefined,
                email: undefined,
                remark: undefined
            },
            customerCode: undefined,
            selectCode: [],
            dialogFormVisible: false,
            dialogFormVisible2: false,
            importDialogVisible: false,//导入弹框
            modalNo: 'EXTP-BAS-0001',//导入模板编号
            dialogStatus: '',
            tableKey: 0,
            isRetailerOptions: TWO_STATE_OPTIONS,
            rules: {
                customerCode: [
                    { required: true, message: '客户编码不能为空', trigger: 'blur' },
                    { pattern: /^[A-Z-_0-9]{1,50}$/, message: '由大写字母，数字，—和_组成，长度不能超过50', trigger: 'blur' }
                ],
                customerName: [
                    { required: true, message: '客户名称不能为空', trigger: 'blur' },
                    { max: 100, message: this.$t('validate.max32'), trigger: 'blur' }
                ],
                contact: [
                    { max: 50, message: '长度不能超过50', trigger: 'blur' }
                ],
                contactNumber: [
                    { validator: validatorContactNumber, trigger: 'blur' }
                ],
                email: [
                    { validator: validatorEmail, trigger: 'blur' }
                ],
                address: [
                    { max: 512, message: '长度不能超过512', trigger: 'blur' }
                ],
                remark: [
                    { max: 512, message: '描述长度为512内', trigger: 'blur' }
                ]
            }
        }
    },
    computed: {
        ...mapState({
            dt_customer_type: state => state.eamDict.dt_customer_type,
            dt_customer_property: state => state.eamDict.dt_customer_property
        })
    },
    mounted () {
        this.$store.dispatch('eamDict/getDicData', ['dt_customer_type', 'dt_customer_property']);
        this.setTableHeight();
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        };
        this.getList();
    },
    methods: {
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight();
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        selectRow (val) {
            this.selectlistRow = val
        },
        resetTemp () {
            this.temp = {
                customerCode: undefined,
                customerName: undefined,
                customerType: undefined,
                customerProperty: undefined,
                isRetailer: 0,
                address: undefined,
                contact: undefined,
                contactNumber: undefined,
                email: undefined,
                remark: undefined
            }
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        }
    }
}
