import { mapState } from "vuex"
import * as api from "@/api/project/srm/supplierManage/supplierAccess/accessEvaluate"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件

import Pagination from "@/components/Pagination"
import dialogConfirm from './dialog/dialogConfirm'
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import dialogGysxz from '@/components/Dialog/dialogsGysxz'//供应商选择
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory'//物料分类选择


export default {
    name: "accessEvaluate",
    components: { caiGou, Pagination, dialogConfirm, dialogPersonSelect, dialogGysxz, dialogMaterielCategory },
    data () {
        return {
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            dataList: [],
            itemList: [],//从表数据
            total: 0,
            totalItem: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 10,
                evaluationNo: null,
                orgCode: null,
                materielClass: null,
                materielClassName: null,
                supplierCode: null,
                supplierName: null,
                state: null,
                par4: null,
                par5: null
            },
            itemQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                evaluationNo: null
            },
            DICT_CODE: DICT_CODE,
            currentSelectedRow: null,//当前选中的行
            selectedRows: [],//多选时选中的所有行
            tableKey: 0, //表格索引
            filterConfig: [
                { type: 'selectcaiGou', label: '采购组织', prop: 'orgCode', width: '', copmNum: 0, class: 'filter-item' },
                { type: 'commonInput', label: '评价单号', prop: 'evaluationNo', width: '', class: 'filter-item ml_5' },
                { type: 'inputBtnMC', label: '物料分类', prop: 'materielClassName', width: '', class: 'ml_5' },
                { type: 'inputBtnGys', label: '供应商', prop: 'supplierName', width: '', class: 'ml_5' },
                { type: 'dictSelect', dType: 'dt_srm_pak', label: '评价类型', prop: 'evaluationType', width: '', optionArr: [], class: 'filter-item ml_5' },//wu
                { type: 'dictSelect', dType: 'dt_srm_pal', label: '状态', prop: 'state', width: '', optionArr: [], class: 'filter-item ml_5' },
                { type: 'inputBtnPerson', label: '评价人', prop: 'evaluateUserName', copmNum: 1, class: 'ml_5' },//wu
            ],
            oprateBtnConfig: [
                { id: 0, class: 'filter-item', icon: 'el-icon-plus', clickMethod: 'handleSubmit', name: '提交' }
            ],
            tableConfig: [
                { label: '状态', prop: 'stateCN', align: 'center', width: 'auto' },
                { label: '评价类型', prop: 'evaluationTypeCN', align: 'center', width: 'auto' },
                { label: '物料分类', prop: 'materielClassName', align: 'center', width: 'auto' },
                { label: '供应商', prop: 'supplierCode', align: 'center', width: '150' },
                { label: '供应商名称', prop: 'supplierName', align: 'center', width: '180' },
                { label: '评价类', prop: 'evaluationItemCN', align: 'center', width: 'auto' },
                { label: '得分', prop: 'score', align: 'center', width: 'auto' },
                { label: '等级', prop: 'gradeCN', align: 'center', width: 'auto' },
                { label: '评价', prop: 'remark', align: 'center', width: 'auto' },
                { label: '评价人', prop: 'evaluationUser', align: 'center', width: 'auto' },
                { label: '提交日期', prop: 'submitDate', align: 'center', width: '150' },
                { label: '组织编码', prop: 'orgCode', align: 'center', width: 'auto' },
                { label: '评价单号', prop: 'evaluationNo', align: 'center', width: '150' },
                { label: '评价计划号', prop: 'evaluationPlanNo', align: 'center', width: '150' },//wu
                { label: '评价模型版本', prop: 'evaluationModel', align: 'center', width: 'auto' }
            ],
            dialogConfig: [
                { title: '评价明细', curdialogStatus: 'judge', unMutiple: false },
                { title: '任务接收人选择', unMutiple: true },
                { title: '供应商信息', isRequired: false, unMutiple: true },
                { title: '选择物料分类信息', isRequired: false, unMutiple: true }
            ]
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_pb4: state => state.dict.dt_srm_pb4,//评价项
            dt_srm_pb5: state => state.dict.dt_srm_pb5,
            dt_srm_pak: state => state.dict.dt_srm_pak,
            dt_srm_pal: state => state.dict.dt_srm_pal,
            dt_cate_grade: state => state.srmBisSelect.dt_cate_grade//综合评价等级
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pb4", "dt_srm_pb5", "dt_srm_pak", "dt_srm_pal"])
        this.$store.dispatch("srmBisSelect/getBisData", ["dt_cate_grade"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList () {
            api.queryItems(this.listQuery).then(res => {
                let options = [this.dt_cate_grade.data, this.dt_srm_pb4, this.dt_srm_pak, this.dt_srm_pal]
                res.data = this.$myFun.transCodeToName(res.data, options, [['grade', 'code', 'name'], 'evaluationItem', 'evaluationType', 'state'])
                this.dataList = res.data.list
                this.total = res.data.pages.count
            })
        },
        handleView (row) {
            this.$refs.refConfig.dialogVisible = true
            api.queryRowItem(row.evaluationNo, row.rowId).then(res => {
                this.dialogConfig[0].curdialogStatus = 'view'
                this.$refs.refConfig.dialogVisible = true
                this.$refs.refConfig.showSave = true
                this.$refs.refConfig.initForm('view', res.data)
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        //操作按钮事件
        oprateClick (id) {
            switch (id) {
                case 0: {
                    this.handleSubmit()
                    break
                }
            }
        },
        //提交操作
        handleSubmit () {
            console.log('提交')
            if (this.selectedRows) {
                if (this.selectedRows.length > 0) {
                    this.$confirm('确认提交吗?', '提示', {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    }).then(() => {
                        let cdata = {
                            gradeItemDTO: {
                                evaluationNo: this.selectedRows[0].evaluationNo,
                                rowId: this.selectedRows[0].rowId
                            },
                            method: 'SUBMIT'
                        }
                        api.updateItemsRecords(cdata.gradeItemDTO.evaluationNo, cdata.gradeItemDTO.rowId, cdata).then(() => {
                            this.$message({ type: 'success', message: '操作成功' })
                            this.getList()
                        })
                    }).catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消'
                        })
                    })
                } else {
                    this.$message({ type: 'warning', message: '请选择一条数据' })
                }
            } else {
                this.$message({ type: 'warning', message: '请选择一条数据' })
            }

        },
        //表格操作编辑
        handleJudge (row) {
            api.queryRowItem(row.evaluationNo, row.rowId).then(res => {
                let options = [this.dt_srm_pb5]
                this.$refs.refConfig.formData.supplierCode = row.supplierCode
                this.$refs.refConfig.formData.supplierName = row.supplierName
                res.data.evaluationDetailDTOs = this.$myFun.codeToName(res.data.evaluationDetailDTOs, options, ['method'])
                this.dialogConfig[0].curdialogStatus = 'judge'
                this.$refs.refConfig.dialogVisible = true
                this.$refs.refConfig.showSave = true
                this.$refs.refConfig.initForm('judge', res.data)
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isSingle) {
                if (val.length > 1) {
                    this.$refs.tb.clearSelection() //清除其他行的选中
                    this.$refs.tb.toggleRowSelection(val[val.length - 1], "selected") //单击行绑定点击事件
                } else if (val.length === 1) {
                    this.selectedRows = val
                    this.currentSelectedRow = val[val.length - 1]
                } else if (val.length === 0) {
                    this.selectedRows = val
                    this.currentSelectedRow = null
                    // this.itemList = []
                }
            } else {
                this.selectedRows = val
                this.currentSelectedRow = val[val.length - 1]
            }

        },
        //确认添加/编辑
        confirmJudge (cdata) {
            console.log(cdata, 'confirmJudge')
            api.updateItemsRecords(cdata.gradeItemDTO.evaluationNo, cdata.gradeItemDTO.rowId, cdata).then(() => {
                this.$message({ type: 'success', message: '操作成功' })
                this.getList()
            })
        },
        showPerson () {
            this.$refs.refPerson.dialogVisible = true
        },
        personConfirm (val) {
            this.$set(this.listQuery, 'evaluateUser', val[0].employeeCode)
            this.$set(this.listQuery, 'evaluateUserName', val[0].employeeName)
        },
        showGys () {
            this.$refs.refGys.dialogVisible = true
        },
        //供应商确认
        handleGysClick (gysRows) {
            this.$set(this.listQuery, 'supplierCode', gysRows[0].supplierCode)
            this.$set(this.listQuery, 'supplierName', gysRows[0].supplierName)
        },
        showMC () {
            if (this.listQuery.orgCode) {
                this.$refs.refMC.listQuery.orgCode = this.listQuery.orgCode
                this.$refs.refMC.selectedRows = []
                this.$refs.refMC.handleQuery()
                this.$refs.refMC.dialogVisible = true
            } else {
                this.$message({ type: 'warning', message: '请先选择采购组织' })
            }

        },
        handleConfirmMC (mdata) {
            this.$set(this.listQuery, 'materielClass', mdata[0].classCode)
            this.$set(this.listQuery, 'materielClassName', mdata[0].className)
        }
    }
};
