import * as api from '@/api/project/eas/er/maintenancePlan'
import { queryRecords } from '@/api/project/eas/database/groups' //班组
import addwxjh from './addwxjh'
import addwxjh2 from './addwxjh2'
import { DT_ORG_TYPE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou' //下拉组件
import teamEmployeeSelect from '../../components/teamEmployeeSelect' //下拉组件
import { mapState } from 'vuex' // 基本数据引入

export default {
    name: 'wxjh',
    components: { addwxjh, addwxjh2, caiGou, teamEmployeeSelect },
    data () {
        return {
            pickerOptions: {
                disabledDate (time) {
                    return time.getTime() <= Date.now() - 8.64e6
                }
            },
            list: [],
            supplierData: [],
            total: 0,
            theight: 0, //表格高度
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            temp: {
                organizationCode: undefined,
                organizationName: undefined,
                maintainType: undefined,
                urgencyDegree: undefined,
                classCode: undefined,
                maintainerCode: undefined,
                maintainerName: undefined,
                planTime: undefined,
                planDays: undefined,
                laborHour: undefined,
                ifOuterHelp: undefined,
                outerHelpOrgCode: undefined,
                outerHelpOrgName: undefined,
                outerHelpLaborHour: undefined,
                remark: undefined,
                details: []
            },
            groupList: [],
            groupManList: [],
            DT_ORG_TYPE: DT_ORG_TYPE,
            importDialogVisible: undefined,
            importDialogVisible2: undefined,
            colorCode: undefined,
            selectlistRow: [],
            dialogFormVisible: false,
            dialogStatus: '',
            teamEmployeeVisible: false,
            tableKey: 0,
            rules: {
                organizationCode: [
                    {
                        required: true,
                        message: '组织编码不能为空',
                        trigger: 'blur'
                    }
                ],
                maintainType: [
                    {
                        required: true,
                        message: '维修类型不能为空',
                        trigger: 'blur'
                    }
                ],
                urgencyDegree: [
                    {
                        required: true,
                        message: '紧急程度不能为空',
                        trigger: 'blur'
                    }
                ],
                classCode: [
                    {
                        required: true,
                        message: '维修班组不能为空',
                        trigger: 'blur'
                    }
                ],
                maintainerCode: [
                    {
                        required: true,
                        message: '维修人不能为空',
                        trigger: 'blur'
                    }
                ],
                planTime: [
                    {
                        required: true,
                        message: '计划时间不能为空',
                        trigger: 'blur'
                    }
                ],
                planDays: [
                    {
                        required: true,
                        message: '计划天数不能为空',
                        trigger: 'blur'
                    },
                    {
                        validator (rule, value, callback) {
                            if (
                                Number.isInteger(Number(value)) &&
                                Number(value) > 0
                            ) {
                                callback()
                            } else {
                                callback(new Error('请输入正整数'))
                            }
                        },
                        trigger: 'blur'
                    }
                ],
                laborHour: [
                    {
                        required: true,
                        message: '日工时不能为空',
                        trigger: 'blur'
                    },
                    {
                        validator (rule, value, callback) {
                            if (
                                Number.isInteger(Number(value)) &&
                                Number(value) > 0
                            ) {
                                callback()
                            } else {
                                callback(new Error('请输入正整数'))
                            }
                        },
                        trigger: 'blur'
                    }
                ],
                ifOuterHelp: [
                    {
                        required: true,
                        message: '是否外协不能为空',
                        trigger: 'blur'
                    }
                ],
                remark: [
                    { max: 512, message: '备注长度为512内', trigger: 'blur' }
                ]
            }
        }
    },
    watch: {
        'temp.organizationCode' (val) {
            if (val) {
                this.listQuery.orgCode = val
                this.getGroup()
            } else {
                this.listQuery.orgCode = undefined
                this.groupList = []
            }
            this.$set(this.temp, 'classCode', undefined)
            this.$set(this.temp, 'maintainerCode', undefined)
        },
        'temp.ifOuterHelp' (val) {
            if (val == 'N') {
                this.temp.outerHelpOrgCode = undefined
                this.temp.outerHelpLaborHour = undefined
            }
        }
    },
    created () {
        this.getList()
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_maintain_type: state => state.eamDict.dt_maintain_type,
            dt_urgency_degree: state => state.eamDict.dt_urgency_degree,
            dt_yes_or_no: state => state.eamDict.dt_yes_or_no
        })
    },
    mounted () {
        this.$store.dispatch('eamDict/getDicData', [
            'dt_maintain_type',
            'dt_urgency_degree',
            'dt_yes_or_no'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getSupplier()
    },
    methods: {
        getTemp () {
            let isv = false
            this.$refs.temp.validate(valid => {
                if (valid) {
                    isv = true
                } else {
                    isv = false
                }
            })
            return isv
        },
        getOuterHelp (val) {
            let arr = this.supplierData.filter(i => {
                return i.supplierCode == val
            })
            this.temp.outerHelpOrgName = arr[0].outerHelpOrgName
        },
        handleTeamEmployee () {
            var tmp = this.$refs.teamEmployeeSelect.selectlistRow
            if (tmp) {
                this.temp.maintainerCode = tmp.employeeCode
                this.temp.maintainerName = tmp.employeeName
            } else {
                this.temp.maintainerCode = undefined
                this.temp.maintainerName = undefined
            }
            this.teamEmployeeVisible = false
        },
        handleMaintainer () {
            if (!this.temp.classCode) {
                this.$message({
                    title: '警告',
                    message: '请选择维修班组',
                    type: 'warning'
                })
                return
            }
            this.teamEmployeeVisible = true
            setTimeout(() => {
                this.$refs.teamEmployeeSelect.getList()
            }, 0)
        },
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //获取班组
        getGroup () {
            queryRecords(this.listQuery).then(res => {
                this.groupList = res.data.list
            })
        },
        //班组选择
        changeClass (val) {
            let name = this.groupList
                .filter(v => v.teamCode == val)
                .find(value => value)
            if (name) {
                this.temp.className = name.teamName
            }
        },
        getSupplier () {
            api.getSuppliers({
                supplierType: 'dt_supplier_type_outer_help',
                currentPage: 1,
                pageSize: 1000
            }).then(res => {
                this.supplierData = res.data.list
            })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleQuery () {
            //查询
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleCreate () {
            //新增
            this.importDialogVisible = true
            setTimeout(() => {
                this.$refs.addwxjh.getListSelected()
                this.$refs.addwxjh.getEquipmentData()
            }, 0)
        },
        handleCreate2 () {
            //新增
            this.importDialogVisible2 = true
            setTimeout(() => {
                this.$refs.addwxjh2.getListSelected()
                this.$refs.addwxjh2.getEquipmentData()
            }, 0)
        },
        // 获取表格选中时的数据
        selectRow (val) {
            this.selectlistRow = val
        },
        getName (val) {
            this.temp.organizationName = val
        },
        handleDelete () {
            this.selectlistRow.forEach(select => {
                this.temp.details.forEach((detail, index) => {
                    if (select.deviceCode == detail.deviceCode) {
                        this.temp.details.splice(index, 1)
                        return
                    }
                })
            })
        },
        cancel () {
            //取消弹窗按钮
            this.dialogFormVisible = false
        },
        resetTemp () {
            this.temp = {
                organizationCode: undefined,
                organizationName: undefined,
                maintainType: undefined,
                urgencyDegree: undefined,
                classCode: undefined,
                maintainerCode: undefined,
                maintainerName: 'aaa',
                planTime: undefined,
                planDays: undefined,
                laborHour: undefined,
                ifOuterHelp: undefined,
                outerHelpOrgCode: undefined,
                outerHelpLaborHour: undefined,
                remark: undefined,
                details: []
            }
        }
    }
}
