@import '~@/styles/iot.scss';

.amain {
    transition: all 2s linear;
}

.charts-bgc {
    background-color: rgb(5, 21, 41);
}

.el-row,
.el-col {
    height: 100%;
}

.el-col {
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
}

.bs-1,
.center-1 {
    flex: 1;
}

.bs-2 {
    flex: 2;
    overflow: hidden;
}

.bs-1,
.bs-2 {
    margin: 5px;
    border: 1px solid rgba(255, 255, 255, 0.6);
    box-shadow: 0 0 2px 0px #fff, 0 0 4px 0 #68e49a;
}

.center-2 {
    flex: 7;
}

.center-1 {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: space-around;
}

.center-1 .info {
    padding: 5px 0;
    width: 20%;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
}

.center-1 .info .title {
    font-weight: 600;
    color: #68e49a;
}

.center-1 .info .number {
    background-image: -webkit-linear-gradient(top, #22b6ed, #3fe279);
    background-clip: text;
    -webkit-text-fill-color: transparent;
    font-weight: 800;
    font-size: 28px;
}

.bar-box .title {
    display: flex;
    justify-content: space-between;
    padding: 2px 3px;
    font-size: 12px;
    color: aquamarine;
}

.carousel-item {
    padding: 0;
    font-size: 14px;
    margin: 0;
}

.carousel-item li {
    padding: 10px;
    list-style: none;
}

.carousel-item li .info {
    color: #fff;
}

.medium .warning {
    color: #d4380d;
}

.medium .success {
    color: #67c23a;
}

.echarts-tooltip-box >>> p {
    display: flex;
}

.echarts-title {
    width: 40%;
    color: #4de6a0ee;
    font-weight: 700;
}
