import Pagination from '@/components/Pagination'
import throttle from 'lodash/throttle'
import { queryAttributes } from "@/api/project/iot/PM/equipmentProducts"
import { mapState } from 'vuex'

export default {
    name: 'attributeSelect',
    components: { Pagination },
    props: {
        isTick: {
            type: Boolean,
            default: false //true 为多选
        },
        code: {
            type: String,
            default: undefined
        },
        // 父组件中已选记录集合
        listSelected: {
            type: Array,
            default: () => {
                return []
            }
        },
    },
    computed: {
        ...mapState({
            iot_attribute_type: state => state.iotDict.iot_attribute_type,
            iot_attribute_company: state => state.iotDict.iot_attribute_company,
            iot_data_type: state => state.iotDict.iot_data_type,
            iot_read_type: state => state.iotDict.iot_read_type,
            iot_is_standard: state => state.iotDict.iot_is_standard,
            iot_state_device: state => state.iotDict.iot_state_device,
        })
    },
    data () {
        return {
            theight: 0, //表格高度
            selectedRows: null, //多选时选中的所有行
            selectlistRow: undefined,//当前选择的
            tableKey: 0, //表格索引，虚拟dom渲染表格时使用
            list: [],
            total: 0,
            listLoading: true,
            listQuery: {
                currentPage: 1,
                pageSize: 15,
                page: 1,
                attributeName: undefined
            }
        }
    },
    watch: {
        'code' () {
            this.getList()
        }
    },
    mounted () {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 100
        },
        getList () {
            //查询
            this.listLoading = true
            queryAttributes(this.code, this.listQuery).then(res => {
                this.list = res.data
                let options = [this.iot_attribute_type, this.iot_data_type, this.iot_read_type, this.iot_attribute_company, this.iot_is_standard, this.iot_state_device]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['attributeType', 'dataType', 'readType', 'company', 'isStandard', 'iotState']
                )
                if (this.listSelected && this.listSelected.length) {
                    this.list.forEach((item) => {
                        this.listSelected.forEach(i => {
                            if (item.attributeCode == i.attributeCode) {
                                this.$nextTick(function () {
                                    this.$refs.tbAtt.toggleRowSelection(item)
                                })
                            }
                        })
                    })

                }
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isTick) {
                this.selectedRows = val
                this.$emit('change', this.selectedRows)
                this.$emit(
                    'input',
                    this.selectedRows.length
                        ? this.selectedRows[0].employeeCode
                        : undefined
                )
            } else {
                this.selectlistRow = val[val.length - 1]
                if (val.length > 1) {
                    this.$refs.tbAtt.clearSelection()
                    this.$refs.tbAtt.toggleRowSelection(val[val.length - 1])
                }
                this.$refs.tbAtt.setCurrentRow(this.selectlistRow)
                this.$emit('change', this.selectlistRow)
                this.$emit(
                    'input',
                    this.selectlistRow
                        ? this.selectlistRow.employeeCode
                        : undefined
                )
            }
        }
    }
}
