import * as api from "@/api/project/srm/supplierManagement/supplierRegister"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import Pagination from "@/components/Pagination"
import dialogMaterielCategory from '@/components/Dialog/dialogMaterielCategory.vue'//主数据物料选择
import dialogPersonSelect from '@/components/Dialog/dialogPersonSelect.vue'//选择人
import { mapState } from "vuex"
export default {
    name: "actuarialList",
    components: { Pagination, caiGou, dialogMaterielCategory, dialogPersonSelect },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            rangeDate: null,
            tabName: 'mainInfo',
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            listSub: [],
            total: 0,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            queryState: ['PA1W', 'PA1Q'],
            dialogVisible: false,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    computed: {
        ...mapState({
            dt_srm_pa3: state => state.dict.dt_srm_pa3,
            dt_srm_pa1: state => state.dict.dt_srm_pa1,
            dt_srm_pdn: state => state.dict.dt_srm_pdn,
            dt_srm_pay: state => state.dict.dt_srm_pay,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pa3", 'dt_srm_pa1', 'dt_srm_pdn', 'dt_srm_pay'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    watch: {
        'dt_srm_pa1': function () {
            this.getList()
        },
        'rangeDate' (value) {
            if (value) {
                this.listQuery.firstExecuteTime = value[0]
                this.listQuery.endExecuteTime = value[1]
            } else {
                this.listQuery.firstExecuteTime = undefined
                this.listQuery.endExecuteTime = undefined
            }
        },
    },

    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        handleClassifyCode () {
            if (this.listQuery.orgCode) {
                this.$refs.refZsjWlxz.listQuery.orgCode = this.listQuery.purchaseOrg
                this.$refs.refZsjWlxz.selectedRows = []
                this.$refs.refZsjWlxz.handleQuery()
                this.$refs.refZsjWlxz.dialogVisible = true
            } else {
                this.$message({ type: 'warning', message: '请先选择采购组织' })
            }
        },
        confirmMateriel (data) {
            this.$set(this.listQuery, 'materialClassification', data[0].classCode)
            this.$set(this.listQuery, 'materialClassificationName', data[0].className)
        },
        handlePerson () {
            this.$refs.person.dialogVisible = true
        },
        //人员选择确定
        personConfirm (val) {
            console.log(val[0].employeeName)
            // this.$set(this.listQuery, 'assignPerson', val[0].employeeCode)
            this.$set(this.listQuery, 'purchaser', val[0].employeeName)
        },
        //获取表格数据
        getList () {
            this.listQuery.platformState = this.queryState.join(",")
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pa1, this.dt_srm_pdn, this.dt_srm_pay]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['platformState', 'confirmState', 'frozenState'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        handleCreate () {
            //新增按钮单击事件方法
            this.$emit('setFormationState', 'add')
            this.$emit('confirmState', 'edit')
        },
        handleConfirm () {
            if (this.selectlistRow) {
                let dto = {
                    method: 'confirm',
                    purchaseOrg: this.selectlistRow.purchaseOrg,
                    purchaser: this.selectlistRow.purchaser
                }
                api.confirmRecord(this.selectlistRow.rowId, dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '信息确认成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })

            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning',
                    duration: 2000
                })
            }
        },
        handleUpdate (index, row) {
            //编辑按钮单击事件方法
            this.$emit('setFormationState', 'edit', row)
            this.$emit('confirmState', 'edit', row)
        },
        handleDelete (index, val) {
            //删除按钮单击事件方法
            api.deleteRecord(val.rowId).then(() => {
                this.$message({
                    title: '成功',
                    message: '删除成功',
                    type: 'success',
                    duration: 2000
                })
                this.getList()
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
