import * as api from "@/api/project/srm/contractManage/specialPaymentMethod"
import Pagination from "@/components/Pagination"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import { mapState } from "vuex"
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
export default {
    name: "proof",
    components: { Pagination, caiGou, AuthoType },
    data () {
        return {
            rangeDate: null,
            DICT_CODE: DICT_CODE,
            theight: 0,
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            list: null,
            listSub: null,
            total: 0,
            totalSub: 0,
            selectedrow: null,//主表选中行
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                warehouseCode: undefined
            },
            tableKey: [0, 1],//表格索引，虚拟dom渲染表格时使用
        }
    },
    computed: {
        ...mapState({
            dt_business_type_screenGroupNum_ww: state => state.businessComponent.dt_business_type_screenGroupNum_ww
        })
    },
    mounted () {
        this.$store.dispatch("businessComponent/getBusinessComponentData", ["dt_business_type_screenGroupNum_ww"])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
            }
        },
        //显示主表
        getList () {
            api.queryRecordsMain(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //确认按钮单击事件
        handleConfirm () {
            console.log('确认')
        },
        //查询
        handleQuery () {
            this.listSub = []
            this.listQuery.currentPage = 1
            this.getList()
        },
        //每页大小变化
        handleSizeChange (val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //当前页变化
        handleCurrentChange (val) {
            this.listQuery.currentPage = val
            this.getList()
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tb.clearSelection()
                this.$refs.tb.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.getSubList()
            }
            this.$refs.tb.setCurrentRow(this.selectlistRow)
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.clearSelection()
            this.$refs.tb.toggleRowSelection(val)
        },
    }
};
