import * as api from '@/api/project/mes/dataBase/equipmentLedger'
import { queryRecords } from '@/api/project/mes/dataBase/work-center'
import Pagination from '@/components/Pagination'
import sbflSelection from './sbflSelection'
import suppierSelection from './suppierSelection'
import personSelection from './personSelection'
export default {
    name: 'edger',
    components: {
        Pagination,
        sbflSelection,
        suppierSelection,
        personSelection
    },
    data() {
        return {
            theight: 0, //表格高度
            isSingle: true, //表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            listQuery: {
                //查询
                currentPage: 1,
                pageSize: 15
            },
            temp: {
                equipmentCode: undefined,
                equipmentMode: undefined,
                equipmentName: undefined,
                equipmentType: undefined,
                factoryCode: undefined,
                lineId: undefined,
                manufacturer: undefined,
                staffId: undefined
            },
            line: null,
            equipmentCode: undefined,
            sbflDialogFormVisible: false,
            supplierDialogFormVisible: false,
            personDialogFormVisible: false,
            dialogFormVisible: false,
            dialogStatus: '',
            currentSelectedRow: null, //当前选中的行
            selectedRows: null, //多选时选中的所有行
            tableKey: 0, //表格索引
            rules: {
                equipmentCode: [
                    {
                        required: true,
                        message: '设备编码不能为空',
                        trigger: 'blur'
                    }
                ],
                equipmentName: [
                    {
                        required: true,
                        message: '设备名称不能为空',
                        trigger: 'blur'
                    }
                ],
                equipmentType: [
                    {
                        required: true,
                        message: '设备分类不能为空',
                        trigger: 'blur'
                    }
                ]
            }
        }
    },
    mounted() {
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight() {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取表格数据
        getList() {
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery() {
            this.listQuery.currentPage = 1
            this.getList()
        },
        handleClose() {
            this.resetTemp()
            this.$refs.temp.resetFields()
            this.getList()
        },
        //表格显示条数变化
        handleSizeChange(val) {
            this.listQuery.pageSize = val
            this.getList()
        },
        //表格当前页变化
        handleCurrentChange(val) {
            this.listQuery.currentPage = val
            this.getList()
        },

        resetTemp() {
            this.temp = {
                equipmentCode: undefined,
                equipmentMode: undefined,
                equipmentName: undefined,
                equipmentType: undefined,
                factoryCode: undefined,
                lineId: undefined,
                manufacturer: undefined,
                staffId: undefined
            }
        },
        handleClicksbfl() {
            //设备分类弹窗
            this.sbflDialogFormVisible = true
        },
        handleDblclickSbfl(row) {
            //设备分类传值
            this.sbflClass = row
        },
        sbflUpdate() {
            //设备分类确定按钮
            if (this.sbflClass) {
                this.temp.equipmentType = this.sbflClass.equipTypeCode
                this.sbflDialogFormVisible = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handleClickSuppier() {
            //打开设备厂商窗口
            this.supplierDialogFormVisible = true
        },
        handleDblclickSupplier(row) {
            //设备厂商传值
            this.suppierRow = row
        },
        supplierUpdate() {
            //设备厂商确定按钮
            if (this.suppierRow) {
                this.temp.manufacturer = this.suppierRow.supplierCode
                this.supplierDialogFormVisible = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handleClickPerson() {
            //打开责任人窗口
            this.personDialogFormVisible = true
        },
        handleDblclickPerson(row) {
            //责任人传值
            this.personRow = row
            console.log(row)
        },
        personUpdate() {
            //责任人确认按钮
            if (this.personRow) {
                this.temp.staffId = this.personRow.userName
                this.personDialogFormVisible = false
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        handleCreate() {
            //新增按钮单击事件方法
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
            queryRecords({
                workCenterClass: 'dt_workcenter_class_line'
            }).then(res => {
                this.line = res.data
            })
        },
        create() {
            this.$refs.temp.validate(valid => {
                if (valid) {
                    //新增确认按钮
                    api.addRecord(this.temp).then(() => {
                        this.getList()
                        this.dialogFormVisible = false
                        this.$message({
                            title: '成功',
                            message: '新增成功',
                            type: 'success',
                            duration: 2000
                        })
                    })
                }
            })
        },
        handleUpdate() {
            //编辑按钮单击事件方法
            if (this.selectedRows && this.selectedRows.length == 1) {
                this.temp = this.selectedRows[0]
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        update() {
            this.$refs.temp.validate(valid => {
                if (valid) {
                    //编辑确认按钮
                    api.updataRecord(this.temp.equipmentCode, this.temp).then(
                        () => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.$message({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        }
                    )
                }
            })
        },
        handleDelete() {
            //删除按钮单击事件方法
            if (this.selectedRows) {
                this.$confirm('此操作将永久删除该文件, 是否继续?', '提示', {
                    confirmButtonText: '确定',
                    cancelButtonText: '取消',
                    type: 'warning'
                })
                    .then(() => {
                        let equipmentCodes = []
                        this.selectedRows.forEach((val, index) => {
                            equipmentCodes[index] = val.equipmentCode
                        })
                        api.deleteRecord(equipmentCodes).then(() => {
                            this.getList(),
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success',
                                    duration: 2000
                                })
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange(val) {
            this.selectedRows = val
        },
        //点击表格某一行
        rowClick(val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        }
    }
}
