import * as api from "@/api/project/srm/procurementDemand/twoelementList"
import Pagination from "@/components/Pagination"
import { mapState } from "vuex"
import { DICT_CODE } from '@/utils/constant'
import caiGou from '@/components/Select/xtpt/caigou'//下拉组件
import addReason from "./dialog/addReason.vue"
import dialogWlxz from '@/components/Dialog/dialogWlxz.vue'//主数据物料选择
import throttle from 'lodash/throttle'

export default {
    name: "twoelementList",
    components: { Pagination, caiGou, addReason, dialogWlxz },
    data () {
        return {
            DICT_CODE: DICT_CODE,
            theight: 0,//表格高度
            isSingle: true,//表格是否单选 点击各按钮根据流程逻辑控制单多选
            list: [],
            total: 0,
            purchaseOrgName: null,
            listQuery: {
                //查询
                page: true,
                currentPage: 1,
                pageSize: 15
            },
            temp: {},
            dialogVisible: false,
            dialogVisibleTwo: false,
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    created () { },
    computed: {
        ...mapState({
            dt_srm_pba: state => state.dict.dt_srm_pba,
            dt_srm_p0: state => state.dict.dt_srm_p0,
        })
    },
    mounted () {
        this.$store.dispatch("dict/getDicData", ["dt_srm_pba", 'dt_srm_p0'])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }

    },
    watch: {
        'dt_srm_pba': function () {
            this.getList()
        }
    },
    methods: {

        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        //获取采购组织名称
        selectName (val) {
            this.purchaseOrgName = val
        },
        //编辑成功之后刷新数据
        refresh () {
            this.getList()
        },
        //获取表格数据
        getList () {
            api.queryRecords(this.listQuery).then(res => {
                let options = [this.dt_srm_pba, this.dt_srm_p0]
                res.data.list = this.$myFun.srmCodeToName(res.data.list, options, ['reason', 'state'])
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //新增按钮单击事件方法
        handleCreate () {
            if (this.listQuery.purchaseOrg && this.listQuery.reason) {
                this.$refs.wlxz.initSelect()
                this.$refs.wlxz.dialogVisible = true
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择采购组织，二元化原因",
                    type: "warning"
                })
            }

        },
        confirmMateriel: throttle(function (val) {
            let needData = val.map(v => {
                let dto = {
                    materialCode: v.materialCode,
                    materialName: v.materialName,
                    purchaseOrg: this.listQuery.purchaseOrg,
                    purchaseOrgName: this.purchaseOrgName,
                    reason: this.listQuery.reason,
                    represent: this.listQuery.represent
                }
                return dto
            })
            api.addRecord(needData).then(() => {
                this.$message({
                    title: '成功',
                    message: '新增成功',
                    type: 'success',
                    duration: 2000
                })
                this.listQuery.purchaseOrg = undefined
                this.listQuery.reason = undefined
                this.listQuery.represent = undefined
                this.getList()
            })
        }, 2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //二元化原因设置
        handleTwo () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                if (this.selectlistRow && this.selectlistRow.length == 1) {
                    this.$refs.addReason.temp = { ...this.selectlistRow[0] }
                } else {
                    this.$refs.addReason.resetTemp()
                }
                this.$refs.addReason.selectlistRow = this.selectlistRow
                this.$refs.addReason.dialogVisibleTwo = true
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择勾选信息",
                    type: "warning"
                })
            }
        },
        //删除按钮单击事件方法
        handleDelete () {
            if (this.selectlistRow && this.selectlistRow.length == 1) {
                console.log(this.selectlistRow)
                this.$confirm("是否删除该信息", "提示", {
                    confirmButtonText: "确定",
                    cancelButtonText: "取消",
                    type: "warning"
                }).then(() => {
                    api.deleteRecord(this.selectlistRow[0].rowId).then(() => {
                        this.$message({
                            title: '成功',
                            message: '删除成功',
                            type: 'success',
                            duration: 2000
                        })
                        this.getList()
                    })
                }).catch(() => {
                    this.$message({
                        type: "info",
                        message: "已取消"
                    })
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        //提交
        handleSubmit () {
            if (this.selectlistRow && this.selectlistRow.length > 0) {
                console.log(this.selectlistRow)
                let dto = {
                    method: 'commit',
                    rowIds: []
                }
                this.selectlistRow.forEach((val, index) => {
                    dto.rowIds[index] = val.rowId
                })
                api.submitRecord(dto).then(() => {
                    this.$message({
                        title: '成功',
                        message: '提交成功',
                        type: 'success',
                        duration: 2000
                    })
                    this.getList()
                })
            } else {
                this.$message({
                    title: "警告",
                    message: "请勾选记录",
                    type: "warning"
                })
            }
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            this.selectlistRow = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val)
        }
    }
};
