import * as api from '@/api/project/eas/el/equipmentSpotCheck'
import Pagination from '@/components/Pagination'

export default {
    name: 'addwxjh',
    components: { Pagination },
    data () {
        return {
            list: null,
            total: 0,
            options: [],
            supplierData: null,
            listQuery: {
                currentPage: 1,
                pageSize: 10
            },
            activeName2: 'first',
            listSelected: [],
            tableKey: 0,
            allowSelect: false
        }
    },
    watch: {
        // 监听时间区间数组 写入到对应字段
        listSelected (value) {
            this.$parent.$parent.temp.details = value
        }
    },
    methods: {
        //查设备类型
        getTypeCode () {
            api.queryTypeCode().then(res => {
                this.options = res.data
            })
        },
        checked (i) {
            this.$refs.tb_bx.toggleRowSelection(this.list[i], true)
        },
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getEquipmentData()
            this.listSelected = []
        },
        getListSelected () {
            this.listSelected = this.$parent.$parent.temp.details
        },
        //检查是否取消勾选
        checkUnsel (val, row) {
            let isuncheck = true
            val.forEach(item => {
                if (item.deviceCode == row.deviceCode) {
                    isuncheck = false
                }
            })
            return isuncheck
        },
        selectMain (val, row) {
            let isuncheck = this.checkUnsel(val, row)
            if (!isuncheck) {
                if (this.listSelected) {
                    val.forEach((items, indexv) => {
                        let isSelected = false
                        this.listSelected.forEach(item => {
                            if (item.deviceCode == items.deviceCode) {
                                isSelected = true
                            }
                        })
                        if (!isSelected) {
                            this.listSelected.push(val[indexv])
                        }
                    })
                } else {
                    this.listSelected = val
                }
            } else {
                this.listSelected = this.listSelected.filter(item => {
                    return item.deviceCode != row.deviceCode
                })
            }
        },
        selectAll (val) {
            if (val.length == 0) {
                this.listSelected = []
                this.$refs.tb_bx.clearSelection()
                return false
            }
            val.forEach((items, indexv) => {
                let isSelected = false
                this.listSelected.forEach(item => {
                    if (item.deviceCode == items.deviceCode) {
                        isSelected = true
                    }
                })
                if (!isSelected) {
                    this.listSelected.push(val[indexv])
                }
            })
        },
        // 在备选中把已选打钩
        getEquipmentData () {
            api.queryMainlist(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
                // 已选中的勾选状态
                if (this.listSelected) {
                    this.listSelected.forEach(items => {
                        this.list.forEach((item, index) => {
                            if (item.deviceCode == items.deviceCode) {
                                this.$nextTick(function () {
                                    this.checked(index)
                                })
                            }
                        })
                    })
                }
            })
        }
    }
}
