import Pagination from '@/components/Pagination' // Secondary package based on el-pagination
import { selectlists } from '@/api/system/employees'
import { selecttree } from '@/api/system/organization'

export default {
    name: 'employeesManagement',
    components: { Pagination },
    props: {
        isTick: {
            type: Boolean,
            default: true //true 为多选
        },
        // 父组件中已选记录集合
        listSelected: {
            type: Array,
            default: () => {
                return []
            }
        },
    },
    data () {
        return {
            theight: 0, //表格高度
            selectedRows: null, //多选时选中的所有行
            selectlistRow: undefined,//当前选择的
            tableKey: 0, //表格索引，虚拟dom渲染表格时使用
            list: [],
            total: 0,
            orgName: [],
            listLoading: true,
            listQuery: {
                currentPage: 1,
                pageSize: 15,
                page: 1,
                scope: false
            },
            readonly: false
        }
    },
    computed: {
        organizationId: {
            get: function () {
                return this.organizationId //获取的时候直接获取值
            },
            set: function (listQuery) {
                this.organizationId = listQuery.organizationName //设置的时候变为大写
            }
        }
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', [
            'dt_org_type',
            'dt_org_function'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() * 2 / 3
        },
        getList () {
            //查询
            this.listLoading = true
            this.listQuery.filterEmployeeCodes = this.listSelected
                .map(item => {
                    return item.maintainerCode
                })
                .join(',')
            selectlists(this.listQuery).then(response => {
                this.list = response.data.list
                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            }),
                selecttree().then(response => {
                    //获取树数据
                    this.treeData = this.$myFun.addTreeRoot(response.data)

                    this.orgName = response.data.map(function (itmes) {
                        return [itmes.rowId, itmes.organizationName]
                    })
                    setTimeout(() => {
                        this.listLoading = false
                    }, 1.5 * 100)
                })
        },
        filertOrgName (val) {
            for (let i = 0; i < this.orgName.length; i++) {
                if (this.orgName[i][0] == val) {
                    return this.orgName[i][1]
                }
                if (val == 0) {
                    return '根目录'
                }
            }
        },
        handleFilter () {
            //查询组织列表
            this.listQuery.page = 1
            this.listLoading = true
            selectlists(this.listQuery).then(response => {
                this.list = response.data.list
                this.total = response.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        // 表格选择框选中 注:参数为选中的所有行的数组
        selectChange (val) {
            if (this.isTick) {
                this.selectedRows = val
                this.$emit('change', this.selectedRows)
                this.$emit(
                    'input',
                    this.selectedRows.length
                        ? this.selectedRows[0].employeeCode
                        : undefined
                )
            } else {
                this.selectlistRow = val[val.length - 1]
                if (val.length > 1) {
                    this.$refs.tb.clearSelection()
                    this.$refs.tb.toggleRowSelection(val[val.length - 1])
                }
                this.$refs.tb.setCurrentRow(this.selectlistRow)
                this.$emit('change', this.selectlistRow)
                this.$emit(
                    'input',
                    this.selectlistRow
                        ? this.selectlistRow.employeeCode
                        : undefined
                )
            }
        }
    }
}
