import * as api from "@/api/project/iot/PM/gatewayProducts"
import { mapState } from 'vuex'
import Pagination from "@/components/Pagination"
import throttle from 'lodash/throttle'
export default {
    name: "targetInfo",
    components: { Pagination },
    props: ['code'],
    data () {
        return {
            theight: 0,//表格高度
            listLoading: false,
            list: [],
            total: 0,
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 15,
                functionExplain: undefined
            },
            temp: {
                functionCategory: undefined,
                functionExplain: undefined,
                jurisdiction: undefined,
                topicClass: undefined,
                remark: undefined
            },
            dialogFormVisible: false,
            dialogFormType: undefined,
            rules: {
                functionExplain: [
                    {
                        required: true,
                        message: '功能说明不能为空',
                        trigger: 'blur'
                    },
                    { max: 100, message: '长度不能超过100', trigger: 'blur' }
                ],
                functionCategory: [
                    {
                        required: true,
                        message: '请选择功能类别',
                        trigger: 'blur'
                    }
                ],
                jurisdiction: [
                    {
                        required: true,
                        message: '请选择操作权限',
                        trigger: 'blur'
                    }
                ],
                topicClass: [
                    {
                        required: true,
                        message: 'topic类不能为空',
                        trigger: 'blur'
                    }
                ]
            },
            currentSelectedRow: null,//当前选中的行
            selectedRows: null,//多选时选中的所有行
            tableKey: 0 //表格索引
        }
    },
    computed: {
        ...mapState({
            iot_function_category: state => state.iotDict.iot_function_category,
            iot_jurisdiction: state => state.iotDict.iot_jurisdiction
        })
    },
    mounted () {
        this.setTableHeight()
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        //获取表格数据
        getList () {
            this.listLoading = true
            api.queryTopicS(this.code, this.listQuery).then(res => {
                this.list = res.data.list
                let options = [this.iot_function_category, this.iot_jurisdiction]
                this.list = this.$myFun.srmCodeToName(
                    this.list,
                    options,
                    ['functionCategory', 'jurisdiction']
                )
                this.total = res.data.pages.count
                setTimeout(() => {
                    this.listLoading = false
                }, 1.5 * 100)
            })
        },
        //表格查询
        handleQuery: throttle(
            function () {
                this.listQuery.currentPage = 1
                this.getList()
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //新增按钮单击事件方法
        handleCreate () {
            this.reTemp()
            this.dialogFormType = 'create'
            this.dialogFormVisible = true
        },
        create: throttle(
            function () {
                this.$refs.tempRef.validate(valid => {
                    if (valid) {
                        api.addTopic(this.code, this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.reTemp()
                            this.$notify({
                                title: '成功',
                                message: '新增成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //编辑按钮单击事件方法
        handleUpdate (row) {
            this.dialogFormType = 'update'
            this.temp = JSON.parse(JSON.stringify(row))
            this.dialogFormVisible = true
        },
        update: throttle(
            function () {
                this.$refs.tempRef.validate(valid => {
                    if (valid) {
                        api.updateTopic(this.code, this.temp.rowId, this.temp).then(() => {
                            this.getList()
                            this.dialogFormVisible = false
                            this.reTemp()
                            this.$notify({
                                title: '成功',
                                message: '更新成功',
                                type: 'success',
                                duration: 2000
                            })
                        })
                    } else {
                        return false
                    }
                })
            },
            2000,
            {
                leading: true,
                trailing: false
            }
        ),
        //删除按钮单击事件方法
        handleDelete (rowId) {
            this.$confirm('此操作将删除所选中数据, 是否继续?', '提示', {
                confirmButtonText: '确定',
                cancelButtonText: '取消',
                type: 'warning'
            })
                .then(() => {
                    api.deleteTopic(this.code, rowId).then(() => {
                        this.getList()
                        this.$message({
                            title: '成功',
                            message: '删除成功',
                            type: 'success',
                            duration: 2000
                        })
                    }).catch(() => {
                        this.getList()
                    })
                })
                .catch(() => {
                    this.$message({
                        type: 'info',
                        message: '已取消删除'
                    })
                })
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight() - 30
        },
        reTemp () {
            this.temp = {
                functionCategory: undefined,
                functionExplain: undefined,
                jurisdiction: undefined,
                topicClass: undefined,
                remark: undefined
            }
        }
    }
};
