import * as api from '@/api/project/spm/database/sparePartsParameter' // 增、删、改api引入
// import { queryRecords } from '@/api/data-base/materiel' // 物料主数据
import Pagination from '@/components/Pagination'
import employeesSelect from '@/components/Select/employeesSelect' //下拉组件
import materialSelect from './materialSelect'
import caigou from '@/components/Select/caigou'
// 
import { mapState } from 'vuex' // 基本数据引入

export default {
    name: 'sparePartsParameter',
    components: {
        Pagination,
        caigou,
        materialSelect,
        employeesSelect
    },
    data () {
        return {
            textarea: '',
            theight: 0, //表格高度
            list: [],
            total: 0,
            tableKey: 0, //表格索引
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                materielCode: undefined,
                materielName: undefined,
                orgCode: undefined
            },
            temp: {
                orgCode: undefined,
                orgName: undefined,
                groupCode: undefined,
                groupName: undefined,
                materielCode: undefined,
                materielName: undefined,
                purchaseEmp: undefined,
                purchaseEmpName: undefined,
                purchaseMeasuringUnit: undefined,
                purchaseMeasuringUnitName: undefined,
                standardPrice: undefined,
                purchaseOrgCode: undefined,
                purchaseOrgName: undefined
            },
            dialogStatus: '',
            employeeDialogVisible: false,
            materialDialogVisible: false,
            dialogFormVisible: false,
            selectedRows: null, //多选时选中的所有行
            rules: {
                orgCode: [
                    {
                        required: true,
                        message: '组织编码不能为空',
                        trigger: ['blur', 'change']
                    }
                ],
                materielCode: [{
                    required: true,
                    message: '物料编码不能为空',
                    trigger: ['blur', 'change']
                }],
                purchaseOrgCode: [{
                    required: true,
                    message: '请选择采购组织',
                    trigger: ['blur', 'change']
                }],
                standardPrice: [
                    {
                        required: true,
                        message: '标准价不能为空',
                        trigger: ['blur', 'change']
                    }
                ],
                groupCode: [{
                    required: true,
                    message: '请选择采购组',
                    trigger: ['blur', 'change']
                }],
                purchaseEmpName: [{
                    required: true,
                    message: '采购员不能为空',
                    trigger: ['blur', 'change']
                }]
            }
        }
    },
    computed: {
        // 过滤器下拉框item数据
        ...mapState({
            dt_requisitions_type: state => state.dict.dt_requisitions_type,
            dt_sp_requisitions_state: state =>
                state.dict.dt_sp_requisitions_state
        })
    },
    mounted () {
        this.$store.dispatch('dict/getDicData', [
            'dt_requisitions_type',
            'dt_sp_requisitions_state'
        ])
        this.setTableHeight()
        //表格高度自适应
        window.onresize = () => {
            this.setTableHeight()
        }
        this.getList()
    },
    methods: {
        // 组织
        getorgCode (name) {
            if (name) this.temp.orgName = name
            else this.temp.orgName = undefined
        },
        // 采购组织
        getPurchaseOrgCode (name) {
            if (name) this.temp.purchaseOrgName = name
            else this.temp.purchaseOrgName = undefined
        },
        // 采购组
        getGroupCode (name) {
            if (name) this.temp.groupName = name
            else this.temp.groupName = undefined
        },
        getMateriel (val, materialDialogVisible) {
            this.materialDialogVisible = materialDialogVisible
            if (val) {
                this.temp.materielCode = val.materielCode
                this.temp.materielName = val.materielName
                this.temp.purchaseMeasuringUnitName = val.measuringUnitName
                this.temp.purchaseMeasuringUnit = val.measuringUnit
            }
        },
        // 选择人员
        handleEmployee (val) {
            if (val.length == 0) return
            if (this.dialogFormVisible) {
                this.temp.purchaseEmp = val[0].employeeCode
                this.temp.purchaseEmpName = val[0].employeeName
            }
        },
        //表格高度计算
        setTableHeight () {
            this.theight = this.$myFun.getSingleTbHeight()
        },
        getList () {
            //获取表格数据
            api.queryRecords(this.listQuery).then(res => {
                this.list = res.data.list
                this.total = res.data.pages.count
            })
        },
        //表格查询
        handleQuery () {
            this.listQuery.currentPage = 1
            this.getList()
        },
        //新增按钮单击事件方法
        handleCreate () {
            this.resetTemp()
            this.dialogStatus = 'create'
            this.dialogFormVisible = true
        },
        //新增确认
        create () {
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    api.addRecords(this.temp).then(res => {
                        if (res.status == 201) {
                            this.$message({
                                title: '成功',
                                message: '新增成功',
                                type: 'success'
                            })
                            this.dialogFormVisible = false
                            this.getList()
                        }
                    })
                }
            })
        },
        // 删除
        handleDelete (row) {
            if (row) {
                this.$confirm(
                    '确定要删除选中的所有请购单吗？, 是否继续?',
                    '提示',
                    {
                        confirmButtonText: '确定',
                        cancelButtonText: '取消',
                        type: 'warning'
                    }
                )
                    .then(() => {
                        api.deleteRecords(row.orgCode, row.materielCode).then(res => {
                            if (res.status == 204) {
                                this.$message({
                                    title: '成功',
                                    message: '删除成功',
                                    type: 'success'
                                })
                                this.getList()
                            }
                        })
                    })
                    .catch(() => {
                        this.$message({
                            type: 'info',
                            message: '已取消删除'
                        })
                    })
            } else {
                this.$message({
                    title: '警告',
                    message: '请选择一条信息',
                    type: 'warning'
                })
            }
        },
        // 编辑行
        handleEditRow (row) {
            api.queryRecord(row.orgCode, row.materielCode).then(res => {
                this.temp = res.data
                this.dialogStatus = 'update'
                this.dialogFormVisible = true
            })
        },
        // 更新
        update () {
            //编辑确认
            this.$refs['temp'].validate(valid => {
                if (valid) {
                    api.patchRecords(
                        this.temp.orgCode,
                        this.temp.materielCode,
                        this.temp
                    ).then(res => {
                        if (res.status == 201) {
                            this.$message({
                                title: '成功',
                                message: '修改成功',
                                type: 'success'
                            })
                            this.dialogFormVisible = false
                            this.getList()
                        }
                    })
                }
            })
        },
        selectChange (val) {
            this.selectedRows = val
        },
        //点击表格某一行
        rowClick (val) {
            this.$refs.tb.toggleRowSelection(val, 'selected') //单击行绑定点击事件
        },
        handleClose () {
            this.resetTemp()
            this.$refs.temp.resetFields()
        },
        resetTemp () {
            this.temp = {
                orgCode: undefined,
                groupCode: undefined,
                materielCode: undefined,
                purchaseEmp: undefined,
                purchaseMeasuringUnit: undefined,
                purchaseMeasuringUnitName: undefined,
                standardPrice: undefined,
                purchaseOrgCode: undefined
            }
        },
    }
}
