import * as api from "@/api/project/srm/contractManage/templateMaintenance"
export default {
    props: ['rowId'],
    components: {},
    data () {
        return {
            dialogVisible: false,
            dialogStatus: '',
            tableKey: 0,
            list: [],
            orderNo: null,
            content: null

        }
    },
    mounted () {
        // this.getList()
    },
    methods: {
        getList () {
            let dto = {
                rowId: this.rowId,
                itemType: 'contract'
            }
            api.queryRecordsItem(dto).then(res => {
                this.list = res.data
            })
        },
        getListData (val) {
            let dto = {
                rowId: val.rowId,
                itemType: 'contract'
            }
            api.queryRecordsItem(dto).then(res => {
                this.list = res.data.list
            })
        },
        handleDelete (index, val) {
            console.log(index, val)
            // api.deleteRecordItem(val.rowId).then(() => {
            //     this.$message({
            //         title: '成功',
            //         message: '删除成功',
            //         type: 'success',
            //         duration: 2000
            //     })
            //     this.getList()
            // })
            this.$confirm("确认要删除该数据项吗?", "提示", {
                confirmButtonText: "确定",
                cancelButtonText: "取消",
                type: "warning"
            }).then(() => {
                this.list.splice(index, 1)
                this.$message({
                    type: "success",
                    message: "删除成功"
                })
            }).catch(() => {
                this.$message({
                    type: "info",
                    message: "已取消"
                })
            })
        },
        handleCreate () {
            this.$emit('create', 'pageSignB')
        },
        handleUpdate (index, val) {
            this.$emit('update', 'pageSignB', val, index)
        },
    }
}
