import Pagination from "@/components/Pagination" // 分页组件
import * as api from "@/api/project/wms/productReturn/productReturnFunc"
import { DT_ORG_TYPE } from "@/utils/constant"
import khxz from "@/components/Select/khxz" //供应商选择
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import qrcode from "@/components/QRCode"
import workCenterSelect from "@/components/Select/work-center-select.vue" //下拉组件
import { mapState } from "vuex"


export default {
    name: "productReturnFunc",
    components: { Pagination, khxz, AuthoType, workCenterSelect, qrcode },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            list: null,
            list1: null,
            list2: null,
            list3: null,
            areaList: null,
            locatorList: [],
            value1: "",
            value2: "",
            value3: "",
            valuedate: "",
            total: 0,
            total1: 0,
            total2: 0,
            total3: 0,
            activeName2: "first",
            queryState: ['0', '1'],
            Edit: false,
            state: true,
            listQuery: {
                currentPage: 1,
                pageSize: 10,
                outOrderPlans: undefined,
                pushTimeStart: undefined,
                executionStatus: undefined,
                orgCode: undefined,
                warehouseCode: undefined,
                method: undefined,
                accountDate: this.timeFormat()
            },
            khxzDialogFormVisible: undefined,
            khxzDialogFormVisible1: undefined,
            listQuery1: {
                currentPage: 1,
                pageSize: 10
            },
            listQuery2: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                executionStatus: undefined,
                pushOrderCode: undefined
            },
            dta: {
                currentPage: 1,
                pageSize: 10
            },
            listQueryProofMain: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                warehouseCode: undefined,
                createDateStart: undefined,
                createDateEnd: undefined
            },
            listQueryProofSub: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined,
                outOrderNumber: undefined,
            },
            //保存数据
            saveTemp: {
                businessType: "",
                valuedate: ""
            },
            dto: {
                accountDate: undefined,
                receiveOrderItemDTOs: undefined,
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                sourceOrderCode: undefined,
                warehouseCode: undefined
            },
            save: undefined,
            isEdit: true,
            locateWarehouseCode: null,
            DT_ORG_TYPE: DT_ORG_TYPE,
            tableKey: [0, 1, 2],
            downloadLoading: false,
            dialogPrintVisible: false,
            printData: {},//打印数据
        }
    },
    computed: {
        ...mapState({
            dt_work_center_workshop: state =>
                state.businessComponent.dt_work_center_workshop,
            dt_return_type_screenGroupNum: state =>
                state.businessComponent.dt_return_type_screenGroupNum
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_work_center_workshop",
            "dt_return_type_screenGroupNum"
        ])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 110
            this.pheight = this.$myFun.getSingleTbHeight() + 55
            this.sheight = this.$myFun.getSingleTbHeight() - 65
        },
        handleTabClick (tab) {//选项卡切换
            if (tab.name == "second") {
                this.getList2()
            } else {
                this.getList()
            }
        },
        handleQuery () {
            if (this.listQuery.orgCode && this.listQuery.warehouseCode && this.listQuery.dicType) {
                this.listQuery.currentPage = 1
                this.getList()
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择组织,仓库,业务类型",
                    type: "warning"
                })
            }

        },
        handleQuery1 () {
            this.listQueryProofMain.currentPage = 1
            this.getList2()
        },
        queryworkshop (v) { //查询车间仓库
            this.listQueryProofMain.workshopCode = v[0].feedArea
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        getList () { //查询出库登记主表
            this.listQuery.executionStatus = this.queryState.join(",")
            this.listQuery.method = 'executions'
            this.list1 = []
            api.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
            })
        },
        checked () {
            this.$refs.dd.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        getList1 () {//查询出库登记从表
            this.listQueryItems.executionStatus = this.queryState.join(",")
            this.listQueryItems.instructionsNo = this.selectlistRow.instructionsNo
            this.listQueryItems.orgCode = this.listQuery.orgCode
            this.listQueryItems.warehouseCode = this.listQuery.warehouseCode
            this.listQueryItems.dicType = this.listQuery.dicType
            api.queryRecorditems(this.listQueryItems).then(res => {
                res = res.data
                res.list.map(v => {
                    v.wareAreaCode = v.wareAreaNo
                    v.wareLocationCode = v.wareLocationNo
                    return v
                })
                res.list.forEach((item, index) => {
                    res.list[index].curQuantity = 0
                    res.list[index].invalidQuantity = res.list[index].instructionsNum - res.list[index].executionsNum
                })
                this.list1 = res.list
                for (let i = 0; i < this.list1.length; i++) {
                    this.locatorList.push([])
                }
                api.getArea(this.locateWarehouseCode).then(resa => {
                    resa = resa.data
                    this.areaList = resa
                })
            })
        },

        getList2 () {//查询出库凭证主表
            // this.listQuery2.businessType='0526'
            if (this.value3) {
                var d1 = this.value3[0]
                var d2 = this.value3[1]
                this.listQueryProofMain.createDateStart = d1
                this.listQueryProofMain.createDateEnd = d2
            } else {
                this.listQueryProofMain.createDateStart = ""
                this.listQueryProofMain.createDateEnd = ""
            }
            this.list3 = []
            api.queryRecordscb(this.listQueryProofMain).then(res => {
                res = res.data
                this.list2 = res.list
                let b = res.list.map(v => {
                    v.pushOrderCode = v.outOrderNumber
                    return v
                })
                this.list2.pushOrderCode = b
                // this.total2=res.pages.count
            })
        },

        getList3 () {//查询出库凭证从表
            this.listQueryProofSub.receiveOrderNumber = this.selectlistRowout.receiveOrderNumber
            api.queryRecordsmx(this.listQueryProofSub).then(res => {
                res = res.data
                this.list3 = res.list
                this.total3 = res.pages.count
            })
        },
        savecheck () {
            let result = true
            let b = this.list1.map(v => {
                v.receiveQuantity = v.curQuantity
                v.sourceOrderNumber = this.selectlistRow.instructionsNo
                v.accountDate = this.listQuery.accountDate
                v.measuringUnitName = this.selectlistRow.measuringUnitName
                v.supplierCode = '*'
                v.sourceRowNo = v.rowNo
                v.toWareAreaCode = '*'
                v.toWareLocationCode = '*'
                return v
            })
            b = this.list1.filter(v => {
                return v.receiveQuantity !== 0
            })
            b.forEach((v) => {
                if (!v.wareAreaCode || !v.wareLocationCode) {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请先选择区位和储位",
                        type: "warning"
                    })
                    result = false
                }
            })
            // this.save=b
            if (result) {
                return b
            } else {
                return false
            }

        },


        //保存
        handleSave () {
            if (this.listQuery.dicType && this.listQuery.accountDate) {
                //save接口
                if (this.selectlistRow) {
                    let saveList = []
                    let result = true
                    let b = this.list1.map(v => {
                        v.receiveQuantity = v.curQuantity
                        v.sourceOrderNumber = this.selectlistRow.instructionsNo
                        v.accountDate = this.listQuery.accountDate
                        v.measuringUnitName = this.selectlistRow.measuringUnitName
                        v.supplierCode = '*'
                        v.sourceRowNo = v.rowNo
                        v.toWareAreaCode = '*'
                        v.toWareLocationCode = '*'
                        return v
                    })
                    b.forEach(item => {
                        if (item.receiveQuantity > 0) {
                            result = false
                            saveList.push(item)
                        }
                    })
                    if (result) {
                        this.$message({
                            title: "警告",
                            message: "本次执行量不可全部为0或空",
                            type: "warning"
                        })
                    } else {
                        this.Edit = true
                        this.dto.receiveOrderItemDTOs = saveList
                        this.dto.businessType = this.listQuery.dicType
                        this.dto.accountDate = this.listQuery.accountDate
                        this.dto.orgCode = this.selectlistRow.orgCode
                        this.dto.sourceOrderCode = this.selectlistRow.pushOrderCode
                        this.dto.warehouseCode = this.selectlistRow.warehouseCode
                        this.dto.supplierCode = '*'
                        this.dto.sourceOrderNumber = this.selectlistRow.instructionsNo
                        api.Save(this.dto).then(() => {
                            this.$notify({
                                title: "成功",
                                message: "保存成功",
                                type: "success",
                                duration: 2000
                            })
                            this.getList1()
                            this.Edit = false
                            this.state = false
                        })
                        if (this.state) {
                            this.Edit = false
                        }
                    }
                } else {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请选择要保存的指示单",
                        type: "warning"
                    })
                }
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择业务类型和记账日期",
                    type: "warning"
                })
            }
        },

        handleRecommend () {
            if (this.selectlistRow3) {
                // let dto={}
                this.dto.businessType = this.listQuery.dicType,
                    this.dto.orgCode = this.selectlistRow.orgCode,
                    this.dto.wareMaterielDTOS = this.selectlistRow3.map(v => {
                        let DTOSlist = {}
                        DTOSlist.materielCode = v.materielCode
                        DTOSlist.materielClassify = ""
                        return DTOSlist
                    }),
                    this.dto.warehouseCode = this.selectlistRow.warehouseCode,
                    api.recommend(this.dto).then(res => {
                        res = res.data
                        res.forEach((val) => {
                            this.list1.forEach((val1, index) => {
                                if (val.materielCode == val1.materielCode) {
                                    this.$set(this.list1[index], 'wareAreaCode', val.areaCode)
                                    this.$set(this.list1[index], 'wareLocationCode', val.locationCode)
                                    this.$set(this.list1, index, this.list1[index])
                                }
                            })
                        })
                    })
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择信息",
                    type: "warning"
                })
            }
        },
        //复制
        handleCopy () {
            if (this.selectlistRow3) {
                this.list1.map((item, index) => {
                    this.selectlistRow3.forEach(items => {
                        if (items.materielCode === item.materielCode) {
                            this.list1[index].curQuantity = item.invalidQuantity
                            this.$set(this.list1, index, this.list1[index])
                        }
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowout) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowout
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.receiveOrderNumber)
                })
            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }
        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加
            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)
        },
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '打印次数', '凭证号', '业务类型',
                    '物料编码', '物料名称', '数量',
                    '组织编码', '生产线', '工位',
                    '仓库编码', '区位', '储位',
                    '记账日期', '创建日期', '记账人',
                    '凭证行', '指示号', '指示行'
                ]
                const filterVal = [
                    'dayin', 'receiveOrderNumber', 'dicType',
                    'materielCode', 'materielName', 'receiveQuantity',
                    'orgCode', 'lineCode', 'station',
                    'warehouseCode', 'wareAreaCode',
                    'wareLocationCode', 'accountDate', 'createTime',
                    'creator', 'sourceOrderCode', 'sourceRowNo',
                ]
                const data = this.formatJson(filterVal, this.list3)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api.getLocator(warehouseCode, areaCode).then(res => {
                res = res.data
                this.$set(this.locatorList, i, res)
                this.$set(this.list1[i], 'wareLocationCode', res[0].locationCode)
                this.$set(this.list1, i, this.list1[i])
            })
        },
        changeAreaSel (areaCode, i) {
            this.getLocatorList(this.locateWarehouseCode, areaCode, i)
        },
        update1 () {//客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQuery.customerCode = this.parentsRows[0].customerCode
                this.khxzDialogFormVisible = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        update2 () { //客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQueryProofMain.customerCode = this.parentsRows[0].customerCode
                this.khxzDialogFormVisible1 = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handleDblclickgysxz (row) {
            //父子页面通信
            this.parentsRows = row
            // this.gysxzDialogFormVisible = false;
        },
        handleClickgysxz () {
            this.khxzDialogFormVisible = true
        },
        handleClickgysxz1 () {
            this.khxzDialogFormVisible1 = true
        },
        selectRow (val) {//出库登记复选框选中方法
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.dd.clearSelection()
                this.$refs.dd.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.$refs.dd.setCurrentRow(this.selectlistRow)
                this.locateWarehouseCode = this.selectlistRow.warehouseCode
                this.getList1()
            }
        },
        selectRow2 (val) { //出库登记单击行选中方法
            this.selectlistRow2 = val
            this.locateWarehouseCode = val.warehouseCode
            this.$refs.dd.clearSelection()
            this.$refs.dd.toggleRowSelection(val)
        },
        selectRow3 (val) { //已选项选中行
            this.selectlistRow3 = val
        },
        selectRowout (val) {//出库凭证复选框选中方法
            this.selectlistRowout = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbmain.clearSelection()
                this.$refs.tbmain.toggleRowSelection(val.pop())
            }
            if (this.selectlistRowout) {
                this.getList3()
            }
            this.$refs.tbmain.setCurrentRow(this.selectlistRowout)
        },
        selectRowout1 (val) {//出库凭证单击行选中方法
            this.selectlistRowout1 = val
            this.$refs.tbmain.clearSelection()
            this.$refs.tbmain.toggleRowSelection(val)
        },
        selectRowchange (val) {
            // this.selectlistRowchange = val;
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.dicType = this.dt_return_type_screenGroupNum.options[0].wareRuleCode
                this.getList()
            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQueryProofMain.orgCode = val
            } else if (i === 3) {
                this.listQueryProofMain.warehouseCode = val
                this.listQueryProofMain.businessType = this.dt_return_type_screenGroupNum.options[0].wareRuleCode
                this.getList2()
            }
        }
    }
};
