import Pagination from "@/components/Pagination" // 分页组件
import * as api from "@/api/project/wms/productIssue/issueOut"
import { DT_ORG_TYPE } from "@/utils/constant"
import khxz from "@/components/Select/khxz" //供应商选择
import AuthoType from "@/components/Select/AuthoType.vue" //权限类型下拉组件
import * as api1 from "@/api/project/wms/productionMaterial/pullerStorage"
import workCenterSelect from "@/components/Select/work-center-select.vue" //下拉组件
import { mapState } from "vuex"
import qrcode from "@/components/QRCode"


export default {
    name: "issueOut",
    components: { Pagination, khxz, AuthoType, workCenterSelect, qrcode },
    data () {
        return {
            rheight: 0,
            pheight: 0,
            sheight: 0,
            list: null,
            subList: null,
            proofMainList: null,
            proofSubList: null,
            areaList: null,
            locatorList: [],
            value1: "",
            value2: "",
            value3: "",
            valuedate: "",
            total: 0,
            subTotal: 0,
            total2: 0,
            total3: 0,
            Edit: false,
            state: true,
            activeName2: "first",
            queryState: ['ADD', 'PART'],
            listQuery: {
                page: true,
                currentPage: 1,
                pageSize: 10,
                outOrderPlans: undefined,
                pushTimeStart: undefined,
                queryStateParam: undefined,
                orgCode: undefined,
                businessType: undefined,
                warehouseCode: undefined,
                pushMaterialState: 'push30',
                accountDate: this.timeFormat()
            },
            khxzDialogFormVisible: undefined,
            khxzDialogFormVisible1: undefined,
            listQuerySub: {
                currentPage: 1,
                pageSize: 10
            },
            listQueryItems: {
                currentPage: 1,
                pageSize: 10,
                queryStateParam: undefined,
                pushOrderCode: undefined
            },
            dta: {
                currentPage: 1,
                pageSize: 10
            },
            listQueryProofMain: {
                currentPage: 1,
                pageSize: 10,
                orgCode: undefined,
                warehouseCode: undefined,
                createDateStart: undefined,
                createDateEnd: undefined
            },
            listQueryProofSub: {
                currentPage: 1,
                pageSize: 10,
                businessType: undefined,
                outOrderNumber: undefined,
            },
            //保存数据
            saveTemp: {
                businessType: "",
                valuedate: ""
            },
            dto: {
                accountDate: undefined,
                outOrderPlans: undefined,
                businessType: undefined,
                customerCode: undefined,
                orgCode: undefined,
                sourceOrderCode: undefined,
                warehouseCode: undefined
            },
            isEdit: true,
            printData: {},//打印数据
            dialogPrintVisible: false,
            locateWarehouseCode: null,
            DT_ORG_TYPE: DT_ORG_TYPE,
            tableKey: 0
        }
    },
    computed: {
        ...mapState({
            dt_work_center_workshop: state =>
                state.businessComponent.dt_work_center_workshop,
            dt_push_type_screenGroupNum: state =>
                state.businessComponent.dt_push_type_screenGroupNum
        })
    },
    mounted () {
        this.setTableHeight()
        this.$store.dispatch("dict/getDicData", [
            "dt_materiel_type",
            "dt_materiel_classify"
        ])
        this.$store.dispatch("businessComponent/getBusinessComponentData", [
            "dt_work_center_workshop",
            "dt_business_type_screenGroupNum",
            "dt_push_type_screenGroupNum"
        ])
    },
    methods: {
        //表格高度计算
        setTableHeight () {
            this.rheight = this.$myFun.getSingleTbHeight() - 165
            this.pheight = this.$myFun.getSingleTbHeight() + 85
            this.sheight = this.$myFun.getSingleTbHeight() - 35
        },
        handleTabClick (tab) {//选项卡切换
            if (tab.name == "second") {
                this.getProofMainList()
            } else {
                this.getList()
            }
        },
        handleQuery () {
            if (this.listQuery.orgCode && this.listQuery.warehouseCode && this.listQuery.businessType) {
                this.listQuery.currentPage = 1
                this.getList()
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择组织，仓库，业务类型",
                    type: "warning"
                })
            }

        },
        handleQuerySub () {
            this.listQueryProofMain.currentPage = 1
            this.getProofMainList()
        },
        timeFormat () {
            var d = new Date()
            var year = d.getFullYear()       //年  
            var month = d.getMonth() + 1     //月  
            var day = d.getDate()            //日  

            var clock = year + "-"
            if (month < 10)
                clock += "0"
            clock += month + "-"

            if (day < 10)
                clock += "0"
            clock += day + " "
            return (clock)
        },
        getList () { //查询出库登记主表
            if (this.value2) {
                var d1 = this.value2
                this.listQuery.pushTimeStart = d1
            }
            this.listQuery.queryStateParam = this.queryState.join(",")
            api.queryRecords(this.listQuery).then(res => {
                res = res.data
                this.list = res.list
                this.total = res.pages.count
                this.$nextTick(function () {
                    this.checked()
                })
                if (this.total == 0) {
                    this.subList = []
                }
            })
        },
        checked () {
            this.$refs.dd.toggleRowSelection(this.list[0], true)//官网方法，重点
        },
        getSubList () {//查询出库登记从表
            this.listQueryItems.queryStateParam = this.queryState.join(",")
            if (this.selectlistRow) {
                this.listQueryItems.pushOrderCode = this.selectlistRow.pushOrderCode
            }
            api.queryRecorditems(this.listQueryItems).then(res => {
                res = res.data
                res.list.forEach((item, index) => {
                    res.list[index].curQuantity = 0
                    res.list[index].invalidQuantity = res.list[index].pushOrderNum - res.list[index].receiveOrderNum
                })
                this.subList = res.list
                for (let i = 0; i < this.subList.length; i++) {
                    this.locatorList.push([])
                }
                api1.getArea(this.locateWarehouseCode).then(resa => {
                    this.areaList = resa
                })
            })
        },

        getProofMainList () {//查询出库凭证主表
            if (this.value3) {
                var d1 = this.value3[0]
                var d2 = this.value3[1]
                this.listQueryProofMain.createDateStart = d1
                this.listQueryProofMain.createDateEnd = d2
            } else {
                this.listQueryProofMain.createDateStart = ""
                this.listQueryProofMain.createDateEnd = ""
            }
            this.proofSubList = []
            api.queryRecordscb(this.listQueryProofMain).then(res => {
                res = res.data
                this.proofMainList = res.list
                let b = res.list.map(v => {
                    v.pushOrderCode = v.outOrderNumber
                    return v
                })
                this.proofMainList.pushOrderCode = b
                // this.total2=res.pages.count
            })
        },

        getList3 () {//查询出库凭证从表
            this.listQueryProofSub.outOrderNumber = this.selectlistRowout.outOrderNumber
            this.listQueryProofSub.businessType = this.selectlistRowout.businessType
            api.queryRecordsmx(this.listQueryProofSub).then(res => {
                res = res.data
                this.proofSubList = res.list
                this.total3 = res.pages.count
            })
        },
        //保存
        handleSave () {
            if (this.listQuery.businessType && this.listQuery.accountDate) {
                //save接口
                if (this.selectlistRow) {
                    let result = true
                    let saveList = []
                    let b = this.subList.map(v => {
                        v.outStockNum = v.curQuantity
                        v.sourceDetailNo = v.rowNo
                        return v
                    })
                    b.forEach(item => {
                        if (item.outStockNum > 0) {
                            result = false
                            this.Edit = true
                            saveList.push(item)

                        }
                    })
                    if (result) {
                        this.$message({
                            title: "警告",
                            message: "本次出库量不可全部为0或空",
                            type: "warning"
                        })
                    } else {
                        this.dto.outOrderPlans = saveList
                        this.dto.businessType = this.listQuery.businessType
                        this.dto.accountDate = this.listQuery.accountDate
                        this.dto.orgCode = this.selectlistRow.orgCode
                        this.dto.customerCode = this.selectlistRow.lineCode
                        this.dto.sourceOrderCode = this.selectlistRow.pushOrderCode
                        this.dto.warehouseCode = this.selectlistRow.warehouseCode
                        api.Save(this.dto).then(() => {
                            this.$notify({
                                title: "成功",
                                message: "保存成功",
                                type: "success",
                                duration: 2000
                            })
                            this.getSubList()
                            this.state = false
                            this.Edit = false
                        })
                        if (this.state) {
                            this.Edit = false
                        }
                    }

                } else {
                    this.$message({
                        title: this.$t("message.warning"),
                        message: "请选择要保存的指示单",
                        type: "warning"
                    })
                }
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: "请先选择业务类型和记账日期",
                    type: "warning"
                })
            }
        },

        //复制
        handleCopy () {
            if (this.selectSubListRow) {
                this.subList.map((item, index) => {
                    this.selectSubListRow.forEach(items => {
                        if (items.materielCode === item.materielCode) {
                            this.subList[index].curQuantity = item.invalidQuantity
                            this.$set(this.subList, index, this.subList[index])
                        }
                    })
                })
            } else {
                this.$message({
                    title: this.$t("message.warning"),
                    message: this.$t("message.checkedplease"),
                    type: "warning"
                })
            }
        },
        //打印
        dayin () {
            if (this.selectlistRowchange) {
                this.dta.businessType = this.selectlistRowchange[0].businessType
                this.dta.outOrderNumber = this.selectlistRowchange[0].outOrderNumber
                this.dta.rowNo = this.selectlistRowchange[0].rowNo
                api.queryRecordsdy(this.dta).then(() => {
                    this.$message({
                        type: "success",
                        message: "打印成功"
                    })
                })
            } else {
                this.$message.warning("请勾选一条信息！")
                return
            }
        },
        //打印
        receiptPrint () {
            if (this.selectlistRowout) {
                this.dialogPrintVisible = true
                this.dialogStatus = "print"
                this.printData = this.selectlistRowout
                this.$nextTick(() => {
                    this.$refs.childQRcode.qrCreate(this.printData.outOrderNumber)
                })

            } else {
                this.$message.warning(this.$t('message.checkedplease'))
                return false
            }

        },
        //打印执行
        doReceiptPrint () {
            let newWin = window.open("")
            var newstr = document.getElementById('receipt').innerHTML
            newWin.document.write(newstr)
            newWin.document.close()//IE添加

            setTimeout(function () {
                newWin.print()
                newWin.close()
            }, 100)
        },
        handleDownload () {
            this.downloadLoading = true
            import('@/vendor/Export2Excel').then(excel => {
                const tHeader = [
                    '凭证号', '物料编码', '物料名称',
                    '数量', '工位', '仓库编码',
                    '区位', '储位', '记账日期',
                    '创建日期', '记账人', '凭证行',
                    '投料单号', '投料单行'
                ]
                const filterVal = [
                    'outOrderNumber', 'materielCode', 'materielName',
                    'outStockNum', 'stationCode', 'warehouseCode',
                    'businessType', 'materielCode', 'accountDate',
                    'createTime', 'creator',
                    'rowNo', 'sourceOrderCode', 'rowId',
                ]
                const data = this.formatJson(filterVal, this.proofMainList)
                excel.export_json_to_excel({
                    header: tHeader,
                    data,
                    filename: 'table-list'
                })
                this.downloadLoading = false
            })
        },
        formatJson (filterVal, jsonData) {
            return jsonData.map(v => filterVal.map(j => {
                if (j === 'timestamp') {
                    return
                } else {
                    return v[j]
                }
            }))
        },
        getLocatorList (warehouseCode, areaCode, i) {
            api1.getLocator(warehouseCode, areaCode).then(res => {
                console.log(res, 'uuu')
                res = res.data
                this.$set(this.locatorList, i, res)
                if (res.length !== 0) {
                    this.$set(this.subList[i], 'wareLocationCode', res[0].locationCode)
                    this.$set(this.subList, i, this.subList[i])
                } else {
                    this.$set(this.subList[i], 'wareLocationCode', null)
                }


            })
        },
        changeAreaSel (areaCode, i) {

            this.getLocatorList(this.locateWarehouseCode, areaCode, i)

        },

        update1 () {//客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQuery.customerCode = this.parentsRows[0].customerCode
                this.khxzDialogFormVisible = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        update2 () { //客户选中弹窗确认按钮
            if (this.parentsRows.length && this.parentsRows.length == 1) {
                this.listQueryProofMain.customerCode = this.parentsRows[0].customerCode
                this.khxzDialogFormVisible1 = false
            } else {
                this.$message({
                    title: "警告",
                    message: "请选择一条信息",
                    type: "warning"
                })
            }
        },
        handleDblclickgysxz (row) {
            //父子页面通信
            this.parentsRows = row
            // this.gysxzDialogFormVisible = false;
        },
        handleClickgysxz () {
            this.khxzDialogFormVisible = true
        },
        handleClickgysxz1 () {
            this.khxzDialogFormVisible1 = true
        },
        selectRow (val) {//出库登记复选框选中方法
            this.selectlistRow = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.dd.clearSelection()
                this.$refs.dd.toggleRowSelection(val.pop())
            }
            if (this.selectlistRow) {
                this.locateWarehouseCode = this.selectlistRow.warehouseCode
                this.getSubList()
            }
        },
        selectRowMain (val) { //出库登记单击行选中方法
            this.selectMainListRow = val
            this.locateWarehouseCode = val.warehouseCode
            this.$refs.dd.clearSelection()
            this.$refs.dd.toggleRowSelection(val)
        },

        selectRowSub (val) { //已选项选中行
            this.selectSubListRow = val
        },
        selectRowout (val) {//出库凭证复选框选中方法
            this.selectlistRowout = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbmain.clearSelection()
                this.$refs.tbmain.toggleRowSelection(val.pop())
            }
            this.$refs.tbmain.setCurrentRow(this.selectlistRowout)
            if (this.selectlistRowout) {
                this.getList3()
            }
        },
        selectRowoutMain (val) {//出库凭证单击行选中方法
            this.selectlistRowout1 = val
            this.$refs.tbmain.clearSelection()
            this.$refs.tbmain.toggleRowSelection(val)
        },
        selectRowchange (val) {
            this.selectlistRowchange = val[val.length - 1]
            if (val.length > 1) {
                this.$refs.tbb.clearSelection()
                this.$refs.tbb.toggleRowSelection(val.pop())
            }
        },
        setdefault (val, i) {
            if (i === 1) {
                this.listQuery.orgCode = val
            } else if (i === 3) {
                this.listQuery.warehouseCode = val
                this.listQuery.businessType = this.dt_push_type_screenGroupNum.options[0].wareRuleCode

            }
        },
        setdefault1 (val, i) {
            if (i === 1) {
                this.listQueryProofMain.orgCode = val
            } else if (i === 3) {
                this.listQueryProofMain.warehouseCode = val
                this.listQueryProofMain.businessType = this.dt_push_type_screenGroupNum.options[0].wareRuleCode
                this.getProofMainList()
            }
        }
    }
};
