<template>
	<div class="v-modal f-dy" v-if="show">
		<transition name="fade">
		<div class="mask" v-if="show1"></div>
		</transition>
		<div class="cts">
			<transition name="bounce">
			<div class="ct wrap" v-if="show1">
				<div class="header">
					删除
				</div>
				<div class="body" @click="right">
					确定删除么???
				</div>
				<div class="footer">
					<a href="" @click.prevent.stop="left">取消</a>
					<a href="" @click.prevent.stop="right" class="crt">确定</a>
				</div>
			</div>
			</transition>
			<span class="span"></span>
		</div>
	</div>
</template>
<script>
export default {
	props:["show"],
	watch:{
		show:function(){
			var _this=this;
			if(this.show){
				setTimeout(function(){
					_this.show1=true;
				},0);
			}
		}
	},
	data:function(){
		return {
			show1:false
		}
	},
	methods:{
		right:function(){
			this.wrap=false;
		},
		left:function(){
			var _this=this;
			_this.show1=false;
			setTimeout(function(){
				_this.$emit("hide");
			},300);
		}
	}	
	
}
</script>
